// export default Dashboard;
import React, { useEffect, useState, useContext } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { FaUserCircle } from "react-icons/fa";
import { AuthContext } from "../auth/AuthContext";
import "./Dashboard.css";

const Dashboard = () => {
    const [rooms, setRooms] = useState([]);
    const navigate = useNavigate();
    const { user, userRole, deptId } = useContext(AuthContext); // ‚úÖ User details from context
    const [userName, setUserName] = useState(localStorage.getItem("userName") || ""); 
    const [currentPassword, setCurrentPassword] = useState("");
    const [newPassword, setNewPassword] = useState("");
    const [showSettings, setShowSettings] = useState(false); // Toggle settings visibility

    useEffect(() => {
        const fetchRooms = async () => {
            try {
                const token = localStorage.getItem("token");
                const response = await axios.get(`http://localhost:8800/api/rooms/${deptId}`, {
                    headers: { Authorization: `Bearer ${token}` },
                });
                setRooms(response.data);
            } catch (error) {
                console.error("‚ùå Error fetching rooms:", error);
            }
        };

        if (deptId) fetchRooms(); // ‚úÖ Fetch only if deptId is available
    }, [deptId]);

    // üîπ Change Password Function
    const handleChangePassword = async (e) => {
        e.preventDefault();
        try {
            const token = localStorage.getItem("token");
            const response = await axios.put("http://localhost:8800/api/users/change-password", {
                user_id: user.user_id,
                currentPassword,
                newPassword,
            }, {
                headers: { Authorization: `Bearer ${token}` },
            });

            alert(response.data.message);
            setCurrentPassword("");
            setNewPassword("");
        } catch (error) {
            console.error("‚ùå Error changing password:", error);
            alert("Failed to change password.");
        }
    };

    // üîπ Logout Function
    const handleLogout = () => {
        localStorage.removeItem("token");
        localStorage.removeItem("userRole");
        localStorage.removeItem("userName");
        navigate("/");
    };

    return (
        <div className="dashboard-container">
            {/* Sidebar */}
            <div className="sidebar">
                <FaUserCircle size={50} className="user-icon" />
                <p className="user-name">{userName}</p>{/* ‚úÖ Display user name */}

                <div className="menu-options">
                    <button className="menu-button" onClick={() => navigate("/dashboard")}>Dashboard</button>

                    {/* ‚úÖ HOD & Admin can manage users */}
                    {(userRole === "hod" || userRole === "admin") && (
                        <button className="menu-button" onClick={() => navigate("/manage-users")}>Manage Users</button>
                    )}

                    {/* ‚úÖ Only Admin can manage inventory */}
                    {userRole === "admin" && (
                        <button className="menu-button" onClick={() => navigate("/manage-inventory")}>Manage Inventory</button>
                    )}

                    {/* ‚úÖ Settings Button */}
                    <button className="menu-button" onClick={() => setShowSettings(!showSettings)}>‚öôÔ∏è Settings</button>

                    {/* ‚úÖ Logout Button */}
                    <button className="menu-button" onClick={handleLogout}>üö™ Logout</button>
                </div>
            </div>

            {/* Main Content */}
            <div className="main-content">
                <h2>Rooms in Your Department</h2>

                {/* Room Grid */}
                <div className="rooms-grid">
                    {["classroom", "lab", "utility"].map((category) => (
                        <div 
                            key={category} 
                            className="room-card" 
                            onClick={() => navigate(`/rooms/${category}`)}
                        >
                            <h3>{category.charAt(0).toUpperCase() + category.slice(1)}s</h3>
                            <p>{rooms.filter(room => room.room_type === category).length} rooms available</p>
                        </div>
                    ))}
                </div>

                {/* üîπ Settings Section (Only Visible When Clicked) */}
                {showSettings && (
                    <div className="settings-container">
                        <h2>‚öôÔ∏è Settings</h2>

                        {/* Change Password Form */}
                        <form onSubmit={handleChangePassword}>
                            <h3>Change Password</h3>
                            <input
                                type="password"
                                placeholder="Current Password"
                                value={currentPassword}
                                onChange={(e) => setCurrentPassword(e.target.value)}
                                required
                            />
                            <input
                                type="password"
                                placeholder="New Password"
                                value={newPassword}
                                onChange={(e) => setNewPassword(e.target.value)}
                                required
                            />
                            <button type="submit">üîí Change Password</button>
                        </form>
                    </div>
                )}
            </div>
        </div>
    );
};

export default Dashboard;
