import React, { useState, useEffect } from "react";
import axios from "axios";

const SuperAdminDashboard = () => {
    const [professors, setProfessors] = useState([]);
    const [selectedProfessor, setSelectedProfessor] = useState("");

    useEffect(() => {
        axios.get("http://localhost:8800/api/professors")
            .then(response => {
                console.log("✅ Professors fetched:", response.data);
                setProfessors(response.data);
            })
            .catch(error => console.error("❌ Error fetching professors:", error));
    }, []);

    const assignHOD = async () => {
        if (!selectedProfessor) {
            alert("Please select a professor to assign as HOD.");
            return;
        }

        try {
            const response = await axios.put("http://localhost:8800/api/superadmin/assign-hod", {
                user_id: selectedProfessor
            });

            console.log("✅ HOD assigned successfully:", response.data);
            alert("HOD assigned successfully!");
            setSelectedProfessor(""); // ✅ Reset selection
        } catch (error) {
            console.error("❌ Error assigning HOD:", error.response ? error.response.data : error);
            alert("❌ Failed to assign HOD.");
        }
    };

    return (
        <div>
            <h2>Super Admin Dashboard</h2>
            <h3>Assign Head of Department (HOD)</h3>

            <label>Select a Professor:</label>
            <select value={selectedProfessor} onChange={(e) => setSelectedProfessor(e.target.value)}>
                <option value="">Select Professor</option>
                {professors.map(prof => (
                    <option key={prof.user_id} value={prof.user_id}>
                        {prof.user_name} - {prof.user_email}
                    </option>
                ))}
            </select>

            <button onClick={assignHOD}>Assign as HOD</button>
        </div>
    );
};

export default SuperAdminDashboard;
