import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import './Departments.css';

const Departments = () => {
    const { faculty_id } = useParams();
    const [departments, setDepartments] = useState([]);
    const [selectedDepartment, setSelectedDepartment] = useState(null);
    const [rooms, setRooms] = useState([]);

    useEffect(() => {
        const fetchAllDepartments = async () => {
            try {
                const res = await axios.get(`http://localhost:8800/departments/${faculty_id}`);
                setDepartments(Array.isArray(res.data) ? res.data : []);
            } catch (err) {
                console.log(err);
                setDepartments([]);
            }
        };
        fetchAllDepartments();
    }, [faculty_id]);

    useEffect(() => {
        if (selectedDepartment) {
            const fetchRooms = async () => {
                try {
                    const res = await axios.get(`http://localhost:8800/rooms/${selectedDepartment.dept_id}`);
                    setRooms(Array.isArray(res.data) ? res.data : []);
                } catch (err) {
                    console.log(err);
                    setRooms([]);
                }
            };
            fetchRooms();
        }
    }, [selectedDepartment]);

    return (
        <div className="departments-container">
            {/* Header */}
            <div className="header">
                <h1>DHARMSINH DESAI UNIVERSITY</h1>
                <input type="text" placeholder="Search here..." className="search-bar" />
                <div className="profile-box"></div>
            </div>

            {/* Departments Navigation */}
            <div className="departments-nav">
                {departments.map((dept) => (
                    <div
                        key={dept.dept_id}
                        className={`department-tab ${selectedDepartment === dept ? 'active' : ''}`}
                        onClick={() => setSelectedDepartment(dept)}
                    >
                        {dept.dept_name}
                    </div>
                ))}
            </div>

            {/* Department Details Section */}
            <div className="department-content">
                {selectedDepartment ? (
                    <div className="department-rooms">
                        {/* Classrooms */}
                        <div className="department-card">
                            <h2>Classrooms</h2>
                            {rooms.filter(room => room.room_type === "classroom").length > 0 ? (
                                rooms.filter(room => room.room_type === "classroom").map(room => (
                                    <p key={room.room_id}>{room.room_name}</p>
                                ))
                            ) : <p>No classrooms available.</p>}
                        </div>

                        {/* Labs */}
                        <div className="department-card">
                            <h2>Labs</h2>
                            {rooms.filter(room => room.room_type === "lab").length > 0 ? (
                                rooms.filter(room => room.room_type === "lab").map(room => (
                                    <p key={room.room_id}>{room.room_name}</p>
                                ))
                            ) : <p>No labs available.</p>}
                        </div>

                        {/* Utility Rooms */}
                        <div className="department-card">
                            <h2>Utility Rooms</h2>
                            {rooms.filter(room => room.room_type === "utility").length > 0 ? (
                                rooms.filter(room => room.room_type === "utility").map(room => (
                                    <p key={room.room_id}>{room.room_name}</p>
                                ))
                            ) : <p>No utility rooms available.</p>}
                        </div>
                    </div>
                ) : (
                    <p>Select a department to view details.</p>
                )}
            </div>
        </div>
    );
};

export default Departments;
