import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import './Departments.css';

const Departments = () => {
    const { faculty_id } = useParams();
    const [departments, setDepartments] = useState([]);
    const [selectedDepartment, setSelectedDepartment] = useState(null);

    useEffect(() => {
        const fetchAllDepartments = async () => {
            try {
                const res = await axios.get(`http://localhost:8800/departments/${faculty_id}`);
                setDepartments(Array.isArray(res.data) ? res.data : []);
            } catch (err) {
                console.log(err);
                setDepartments([]);
            }
        };
        fetchAllDepartments();
    }, [faculty_id]);

    return (
        <div className="departments-container">
            {/* Header */}
            <div className="header">
                <h1>DHARMSINH DESAI UNIVERSITY</h1>
                <input type="text" placeholder="Search here..." className="search-bar" />
                <div className="profile-box"></div>
            </div>

            {/* Departments Navigation */}
            <div className="departments-nav">
                {departments.map((dept) => (
                    <div
                        key={dept.dept_id}
                        className={`department-tab ${selectedDepartment === dept ? 'active' : ''}`}
                        onClick={() => setSelectedDepartment(dept)}
                    >
                        {dept.dept_name}
                    </div>
                ))}
            </div>

            {/* Department Details Section */}
            <div className="department-content">
                {selectedDepartment ? (
                    <div className="department-card">
                        <div className="department-icon"></div>
                        <h2>{selectedDepartment.dept_name}</h2>
                        <p>Details about {selectedDepartment.dept_name}.</p>
                    </div>
                ) : (
                    <p>Select a department to view details.</p>
                )}
            </div>
        </div>
    );
};

export default Departments;
