// export default App;
import {Routes, Route } from "react-router-dom";
import "./App.css";
import { useState, useEffect } from "react";
import { jwtDecode } from "jwt-decode";
import axios from "axios";

import Dashboard from "./pages/Dashboard";
import SuperAdminDashboard from "./pages/SuperAdminDashboard";
import Departments from "./pages/Departments";
import Update from "./pages/Update";
import Add from "./pages/Add";
import Login from "./pages/Login";
import ChangePassword from "./pages/ChangePassword";

function App() {
    const [user, setUser] = useState(null);
    const [userId, setUserId] = useState(""); // ✅ Store user_id for password change


useEffect(() => {
    try {
        const storedToken = window.localStorage.getItem("google_token");

        // ✅ Check if token exists and is properly formatted
        if (storedToken && storedToken.split(".").length === 3) {
            const decodedUser = jwtDecode(storedToken);
            setUser(decodedUser);
        } else {
            console.warn("⚠️ Invalid or missing token. Logging out.");
            window.localStorage.removeItem("google_token"); // ✅ Remove bad token
            setUser(null);
        }
    } catch (error) {
        console.error("❌ Error decoding token:", error);
        window.localStorage.removeItem("google_token"); // ✅ Remove bad token
        setUser(null);
    }

    const handleLogin = (credentials) => {
        axios.post("http://localhost:8800/api/login", credentials)
            .then(response => {
                const token = response.data.token; // ✅ Get token from backend
                window.localStorage.setItem("google_token", token); // ✅ Store token
                setIsLoggedIn(true);
                setUserId(response.data.user.user_id);
                alert("Login successful!");

                // ✅ Redirect based on role
                const userRole = response.data.user.role;
                if (userRole === "super_admin") {
                    navigate("/superadmin");
                } else {
                    navigate("/dashboard");
                }
            })
            .catch(error => {
                console.error("❌ Login error:", error);
                alert("❌ Invalid email or password.");
            });
    };

}, []);

    return (

            <div className="App">
                <header className="App-header">
                    <Routes>
                        <Route path="/" element={<Login setUserId={setUserId} />} /> {/* ✅ Pass setUserId */}
                        <Route path="/dashboard" element={<Dashboard />} />
                        <Route path="/superadmin" element={<SuperAdminDashboard />} />
                        <Route path="/add" element={<Add />} />
                        <Route path="/update/:id" element={<Update />} />
                        <Route path="/departments/:faculty_id" element={<Departments />} />
                        <Route path="/changepassword" element={<ChangePassword user_id={userId} />} />
                    </Routes>
                </header>
            </div>

    );
}

export default App;
