import { BrowserRouter, Routes, Route } from "react-router-dom";
import "./App.css";
import { useState, useEffect } from "react";
import { jwtDecode } from "jwt-decode";
import { GoogleOAuthProvider } from "@react-oauth/google";

import Dashboard from "./pages/Dashboard";
import Departments from "./pages/Departments";
import Update from "./pages/Update";
import Add from "./pages/Add";
import Login from "./pages/Login";
import Logout from "./pages/Logout";
import Register from "./pages/Register";

const clientId = process.env.REACT_APP_CLIENT_ID;

function App() {
    const [user, setUser] = useState(null);
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const [users, setUsers] = useState([]);

    useEffect(() => {
        const storedToken = window.localStorage.getItem("google_token");
        if (storedToken) {
            const decodedUser = jwtDecode(storedToken);
            setUser(decodedUser);
        }
    }, []);

    const responseGoogle = (res) => {
        if (res?.credential) {
            const decodedUser = jwtDecode(res.credential);
            setUser(decodedUser);
            window.localStorage.setItem("google_token", res.credential);
        } else {
            setUser(null);
            window.localStorage.removeItem("google_token");
        }
    };

    const handleLogin = (credentials) => {
        const foundUser = users.find(
            (u) => u.email === credentials.email && u.password === credentials.password
        );
        if (foundUser) {
            setIsLoggedIn(true);
            alert("Login successful!");
        } else {
            alert("Invalid email or password");
        }
    };

    const handleRegister = (newUser) => {
        const userExists = users.some((u) => u.email === newUser.email);
        if (userExists) {
            alert("User already exists");
            return;
        }
        setUsers([...users, newUser]);
        alert("Registration successful! Please login.");
    };

    return (
        <GoogleOAuthProvider clientId={clientId}>
            <BrowserRouter>
                <div className="App">
                    <header className="App-header">
                        <Routes>
                            <Route path="/" element={<Dashboard />} />
                            <Route path="/add" element={<Add />} />
                            <Route path="/update/:id" element={<Update />} />
                            <Route path="/departments/:faculty_id" element={<Departments />} />
                            <Route path="/login" element={<Login onLogin={handleLogin} responseGoogle={responseGoogle} />} />
                            <Route path="/register" element={<Register onRegister={handleRegister} responseGoogle={responseGoogle} />} />
                        </Routes>
                        {user || isLoggedIn ? (
                            <Logout response={responseGoogle} isLoggedIn={isLoggedIn} setIsLoggedIn={setIsLoggedIn} />
                        ) : null}
                    </header>
                </div>
            </BrowserRouter>
        </GoogleOAuthProvider>
    );
}

export default App;
