import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "./Login.css";

const Login = ({ setUserId }) => {
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const navigate = useNavigate();

    const handleSubmit = async (e) => {
        e.preventDefault();

        if (!email || !password) {
            alert("‚ùå Please fill in all fields");
            return;
        }

        try {
            const response = await axios.post("http://localhost:8800/api/login", {
                email,
                password
            });

            console.log("‚úÖ Login successful:", response.data);
            alert("üéâ Login successful!");

            const { token, user } = response.data;
            localStorage.setItem("token", token); // ‚úÖ Store token for persistence
            setUserId(user.user_id); // ‚úÖ Update userId

            if (user.role === "super_admin") {
                navigate("/superadmin");
            } else {
                navigate("/dashboard");
            }

        } catch (error) {
            console.error("‚ùå Login failed:", error.response ? error.response.data : error);
            alert("‚ùå Invalid email or password.");
        }
    };

    return (
        <div>
            <h2>Login</h2>
            <form onSubmit={handleSubmit} className="form-container">
                <div>
                    <label>Email:</label>
                    <input
                        type="email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        placeholder="Enter your email"
                    />
                </div>
                <div>
                    <label>Password:</label>
                    <input
                        type="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        placeholder="Enter your password"
                    />
                </div>
                <button type="submit">Login</button>
            </form>
        </div>
    );
};

export default Login;
