import React, { useState } from "react";
import { GoogleOAuthProvider, GoogleLogin } from "@react-oauth/google";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "./Login.css";

const Login = ({ responseGoogle }) => {
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const clientId = process.env.REACT_APP_CLIENT_ID;
    const navigate = useNavigate();

    const handleSubmit = async (e) => {
        e.preventDefault();

        if (!email || !password) {
            alert("Please fill in all fields");
            return;
        }

        try {
            const response = await axios.post("http://localhost:8800/api/login", { email, password });

            console.log("✅ Login successful:", response.data);
            alert("🎉 Login successful!");

            const userRole = response.data.user.role; // Get user role from response

            if (userRole === "super_admin") {
                navigate("/superadmin"); // ✅ Redirect super admin
            } else {
                navigate("/dashboard"); // ✅ Redirect other users
            }

        } catch (error) {
            console.error("❌ Login failed:", error.response ? error.response.data : error);
            alert("❌ Invalid email or password.");
        }
    };

    const handleGoogleLogin = (response) => {
        console.log("Google Login Response:", response);
        if (response.credential) {
            window.localStorage.setItem("google_token", response.credential);
            responseGoogle(response);
            navigate("/dashboard"); // ✅ Google users go to the main dashboard
        }
    };

    return (
        <GoogleOAuthProvider clientId={clientId}>
            <div>
                <h2>Login</h2>
                <form onSubmit={handleSubmit} className="form-container">
                    <div>
                        <label>Email:</label>
                        <input
                            type="email"
                            value={email}
                            onChange={(e) => setEmail(e.target.value)}
                            placeholder="Enter your email"
                        />
                    </div>
                    <div>
                        <label>Password:</label>
                        <input
                            type="password"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            placeholder="Enter your password"
                        />
                    </div>
                    <button type="submit">Login</button>
                </form>

                <div className="google-login-container">
                    <GoogleLogin onSuccess={handleGoogleLogin} onError={() => console.log("Login Failed")} />
                </div>
            </div>
        </GoogleOAuthProvider>
    );
};

export default Login;
