import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import './Departments.css';

const Departments = () => {
    const { faculty_id } = useParams();
    const [departments, setDepartments] = useState([]);
    const [selectedDepartmentId, setSelectedDepartmentId] = useState(null);
    const [rooms, setRooms] = useState([]);

    useEffect(() => {
        // Fetch all departments
        const fetchAllDepartments = async () => {
            try {
                const res = await axios.get(`http://localhost:8800/departments/${faculty_id}`);
                setDepartments(Array.isArray(res.data) ? res.data : []);
            } catch (err) {
                console.log(err);
                setDepartments([]);
            }
        };
        fetchAllDepartments();
    }, [faculty_id]);

    useEffect(() => {
        // Fetch rooms for the selected department
        const fetchRooms = async () => {
            if (!selectedDepartmentId) return;
            try {
                const res = await axios.get(`http://localhost:8800/rooms/${selectedDepartmentId}`);
                setRooms(Array.isArray(res.data) ? res.data : []);
            } catch (err) {
                console.log(err);
                setRooms([]);
            }
        };
        fetchRooms();
    }, [selectedDepartmentId]);

    return (
        <div className="departments-container">
            {/* Header */}
            <div className="header">
                <h1>DHARMSINH DESAI UNIVERSITY</h1>
                <input type="text" placeholder="Search here..." className="search-bar" />
                <div className="profile-box"></div>
            </div>

        {/* Departments Navigation (Now Horizontal) */}
<div className="departments-nav">
    {departments.map((dept) => (
        <div
            key={dept.dept_id}
            className={`department-tab ${selectedDepartmentId === dept.dept_id ? 'active' : ''}`}
            onClick={() => setSelectedDepartmentId(dept.dept_id)}
        >
            {dept.dept_name}
        </div>
    ))}
</div>



            {/* Department Details Section */}
            <div className="department-content">
                {selectedDepartmentId ? (
                    <div className="department-rooms">
                        {['classroom', 'lab', 'utility'].map((roomType) => {
                            const filteredRooms = rooms.filter(room => room.room_type === roomType);
                            return (
                                <div className="department-card" key={roomType}>
                                    <h2>{roomType.charAt(0).toUpperCase() + roomType.slice(1)}</h2>
                                    {filteredRooms.length > 0 ? (
                                        filteredRooms.map(room => (
                                            <p key={room.room_id}>{room.room_name}</p>
                                        ))
                                    ) : <p>No {roomType}s available.</p>}
                                </div>
                            );
                        })}
                    </div>
                ) : (
                    <p>Select a department to view details.</p>
                )}
            </div>
        </div>
    );
};

export default Departments;
