import { createContext, useState, useEffect } from "react";
import { jwtDecode } from "jwt-decode";

export const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
    const [user, setUser] = useState(null);
    const [userId, setUserId] = useState("");
    const [userRole, setUserRole] = useState(""); // ✅ Store user role

    useEffect(() => {
        try {
            const storedToken = window.localStorage.getItem("token"); // ✅ Use correct token key

            if (storedToken && storedToken.split(".").length === 3) {
                const decodedUser = jwtDecode(storedToken);
                setUser(decodedUser);
                setUserId(decodedUser.user_id);
                setUserRole(decodedUser.dept); // ✅ Store user role
            } else {
                window.localStorage.removeItem("token"); // ✅ Use correct token key
                setUser(null);
                setUserId("");
                setUserRole("");
            }
        } catch (error) {
            console.error("❌ Error decoding token:", error);
            window.localStorage.removeItem("token");
            setUser(null);
            setUserId("");
            setUserRole("");
        }
    }, []);

    return (
        <AuthContext.Provider value={{ user, setUser, userId, setUserId, userRole, setUserRole }}>
            {children}
        </AuthContext.Provider>
    );
};
