import React, { useEffect, useState, useContext } from "react";
import axios from "axios";
import "./ManageInventory.css";
import { AuthContext } from "../auth/AuthContext";

const ManageInventory = () => {
    const { deptId, userRole } = useContext(AuthContext); // Get deptId from AuthContext
    const [inventory, setInventory] = useState([]);
    const [selectedItem, setSelectedItem] = useState(null);
    const [itemName, setItemName] = useState("");
    const [status, setStatus] = useState("");
    const [quantity, setQuantity] = useState("");
    const [location, setLocation] = useState("");
    const [category, setCategory] = useState("");
    const [lastMaintenance, setLastMaintenance] = useState("");

    useEffect(() => {
        fetchInventory();
    }, []);

    const fetchInventory = async () => {
        try {
            const token = localStorage.getItem("token");
            const response = await axios.get(`http://localhost:8800/api/inventory`, {
                headers: { Authorization: `Bearer ${token}` },
            });
            setInventory(response.data);
        } catch (error) {
            console.error("‚ùå Error fetching inventory:", error);
        }
    };

    const handleAddItem = async (e) => {
        e.preventDefault();
        try {
            const token = localStorage.getItem("token");
            await axios.post(
                "http://localhost:8800/api/inventory",
                {
                    item_name: itemName,
                    category,
                    quantity,
                    location,
                    status,
                    last_maintenance: lastMaintenance,
                },
                { headers: { Authorization: `Bearer ${token}` } }
            );
            alert("‚úÖ Inventory item added successfully!");
            fetchInventory();
            resetForm();
        } catch (error) {
            console.error("‚ùå Error adding item:", error);
        }
    };

    const handleUpdateItem = async () => {
        if (!selectedItem) return;
        try {
            const token = localStorage.getItem("token");
            await axios.put(
                `http://localhost:8800/api/inventory/${selectedItem}`,
                { item_name: itemName, category, quantity, location, status, last_maintenance: lastMaintenance },
                { headers: { Authorization: `Bearer ${token}` } }
            );
            alert("‚úÖ Inventory item updated successfully!");
            fetchInventory();
            resetForm();
        } catch (error) {
            console.error("‚ùå Error updating item:", error);
        }
    };

    const handleDeleteItem = async (itemId) => {
        if (!window.confirm("Are you sure you want to delete this item?")) return;
        try {
            const token = localStorage.getItem("token");
            await axios.delete(`http://localhost:8800/api/inventory/${itemId}`, {
                headers: { Authorization: `Bearer ${token}` },
            });
            alert("‚úÖ Inventory item deleted successfully!");
            fetchInventory();
        } catch (error) {
            console.error("‚ùå Error deleting item:", error);
        }
    };

    const resetForm = () => {
        setItemName("");
        setCategory("");        
        setQuantity("");
        setLocation("");
        setStatus("");
        setLastMaintenance("");
        setSelectedItem(null);
    };

    return (
        <div className="manage-inventory-container">
            <h2>Manage Inventory</h2>

            {userRole === "admin" && (
                <div className="inventory-form">
                    <h3>{selectedItem ? "Update Item" : "Add New Item"}</h3>
                    <form onSubmit={selectedItem ? handleUpdateItem : handleAddItem}>
                        <input type="text" placeholder="Item Name" value={itemName} onChange={(e) => setItemName(e.target.value)} required />
                        <input type="text" placeholder="Category" value={category} onChange={(e) => setStatus
                (e.target.value)} required />
                        <input type="number" placeholder="Quantity" value={quantity} onChange={(e) => setQuantity(e.target.value)} required />
                        <input type="text" placeholder="Location" value={location} onChange={(e) => setLocation(e.target.value)} required />
                        <input type="text" placeholder="Condition" value={category} onChange={(e) => setCondition(e.target.value)} required />
                        <input type="date" placeholder="Last Maintenance Date" value={lastMaintenance} onChange={(e) => setLastMaintenance(e.target.value)} />
                        <button type="submit">{selectedItem ? "Update Item" : "Add Item"}</button>
                    </form>
                </div>
            )}

            <h3>Inventory List</h3>
            <table className="inventory-table">
                <thead>
                    <tr>
                        <th>Item Name</th>
                        <th>Category</th>
                        <th>Quantity</th>
                        <th>Location</th>
                        <th>Condition</th>
                        <th>Last Maintenance</th>
                        {userRole === "admin" && <th>Actions</th>}
                    </tr>
                </thead>
                <tbody>
                    {inventory.map((item) => (
                        <tr key={item.item_id}>
                            <td>{item.item_name}</td>
                            <td>{item.category}</td>
                            <td>{item.quantity}</td>
                            <td>{item.location}</td>
                            <td>{item.status}</td>
                            <td>{item.last_maintenance}</td>
                            {userRole === "admin" && (
                                <td>
                                    <button onClick={() => { 
                                        setSelectedItem(item.item_id); 
                                        setItemName(item.item_name);
                                        setStatus
                            (item.category);
                                 setQuantity(item.quantity);
                                        setLocation(item.location);
                                        setCondition(item.status);
                                        setLastMaintenance(item.last_maintenance);
                                    }}>‚úèÔ∏è Edit</button>
                                    <button onClick={() => handleDeleteItem(item.item_id)}>üóëÔ∏è Delete</button>
                                </td>
                            )}
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default ManageInventory;
