import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { FaUserCircle } from 'react-icons/fa';
//import { useAuth } from '../auth/AuthContext'; // üîπ Import authentication context
import './Dashboard.css';

const Dashboard = () => {
    const [faculties, setFaculties] = useState([]);
    const navigate = useNavigate();
    const { userRole } = useAuth(); // üîπ Get user role

    useEffect(() => {
        const fetchAllFaculties = async () => {
            try {
                const res = await axios.get("http://localhost:8800/api/faculties");
                setFaculties(res.data);
            } catch (err) {
                console.error("‚ùå Error fetching faculties:", err);
            }
        };
        fetchAllFaculties();
    }, []);

    return (
        <div className="dashboard-container">
            {/* Sidebar */}
            <div className="sidebar">
                <FaUserCircle size={50} className="user-icon" />
                <div className="menu-options">
                    <button className="menu-button">Dashboard</button>
                    
                    {/* üîπ Show Manage Users only for HODs & Admins */}
                    {userRole === "HOD" || userRole === "Admin" ? (
                        <>
                            <button className="menu-button" onClick={() => navigate("/manage-users")}>
                                Manage Users
                            </button>
                            <button className="menu-button" onClick={() => navigate("/manage-inventory")}>
                                Manage Inventory
                            </button>
                        </>
                    ) : null}

                    <button className="menu-button">Profile</button>
                    <button className="menu-button">Settings</button>
                    <button className="menu-button">Logout</button>
                </div>
            </div>

            {/* Main Content */}
            <div className="main-content">
                {/* Header */}
                <div className="header">
                    <h1>DHARMSINH DESAI UNIVERSITY</h1>
                    <div className="profile-box"></div>
                </div>

                {/* Faculties Section */}
                <div className="faculties-grid">
                    {faculties
                        .filter(faculty => faculty.f_name.toLowerCase() !== "dduadmin") // ‚úÖ Hide "Admin" faculty
                        .map(faculty => (
                            <div 
                                key={faculty.faculty_id} 
                                className="faculty-card"
                                onClick={() => navigate(`/departments/${faculty.faculty_id}`)}
                            >
                                <div className="faculty-icon">üèõÔ∏è</div>
                                <button className="faculty-name">{faculty.f_name}</button>
                            </div>
                        ))
                    }
                </div>
            </div>
        </div>
    );
};

export default Dashboard;
