import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import "./InventoryTable.css";

const InventoryTable = () => {
    const { roomId } = useParams();
    const [inventory, setInventory] = useState([]);
    const [searchTerm, setSearchTerm] = useState("");

    useEffect(() => {
        const fetchInventory = async () => {
            try {
                const token = localStorage.getItem("token");
                const response = await axios.get(`http://localhost:8800/api/inventory/${roomId}`, {
                    headers: { Authorization: `Bearer ${token}` },
                });
                setInventory(response.data);
            } catch (error) {
                console.error("‚ùå Error fetching inventory:", error);
            }
        };
        if (roomId) fetchInventory();
    }, [roomId]);

    return (
        <div className="inventory-container">
            <h2>Inventory for Room ID: {roomId}</h2>

            <input
                type="text"
                placeholder="üîç Search items..."
                className="search-bar"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
            />

            <table className="inventory-table">
                <thead>
                    <tr>
                        <th>Item Name</th>
                        <th>Item ID</th>
                        <th>Category</th>
                        <th>Quantity</th>
                        <th>Condition</th>
                        <th>Last Maintenance</th>
                    </tr>
                </thead>
                <tbody>
                    {inventory
                        .filter((item) =>
                            item.i_name.toLowerCase().includes(searchTerm.toLowerCase())
                        )
                        .map((item) => (
                            <tr key={item.item_id}>
                                <td>{item.i_name}</td>
                                <td>{item.item_id}</td>
                                <td>{item.category}</td>
                                <td>{item.quantity}</td>
                                <td>{item.status}</td>
                                <td>{item.last_maintenance_date}</td>
                            </tr>
                        ))}
                </tbody>
            </table>
        </div>
    );
};

export default InventoryTable;
