import React, { useEffect, useState, useContext } from "react";
import axios from "axios";
import "./ManageUsers.css";
import { AuthContext } from "../auth/AuthContext";
import { useParams ,useNavigate} from "react-router-dom";
import { FaUserCircle } from "react-icons/fa";

const ManageUsers = () => {
    const [users, setUsers] = useState([]);
    const [selectedUser, setSelectedUser] = useState(null);
    const [username, setUsername] = useState("");
    const [email, setEmail] = useState("");
    const [role, setRole] = useState("");
    const [researchArea, setResearchArea] = useState(""); // For professors
    const [password, setPassword] = useState(""); // New password field
    const { deptId, userRole, userId } = useContext(AuthContext);
      const navigate = useNavigate();  
        const [userName, setUserName] = useState(localStorage.getItem("userName") || "");

    useEffect(() => {
        fetchUsers();
    }, []);
    
    const fetchUsers = async () => {
        try {
            const token = localStorage.getItem("token");
            const res = await axios.get("http://localhost:8800/api/users", {
                headers: { Authorization: `Bearer ${token}` }
            });

            // üîπ Exclude the logged-in user from the list
            const filteredUsers = res.data.filter(user => user.user_id !== userId);
            setUsers(filteredUsers);
        } catch (err) {
            console.error("‚ùå Error fetching users:", err);
            alert("‚ùå Failed to fetch users.");
        }
    };

    // ‚úÖ Handle Adding a New User
    const handleAddUser = async (e) => {
        e.preventDefault();

        try {
            const token = localStorage.getItem("token");
            if (!token) {
                alert("‚ùå Unauthorized: Please log in again.");
                return;
            }

            if (!deptId) {
                alert("‚ùå Department information missing. Please re-login.");
                return;
            }

            const response = await axios.post(
                "http://localhost:8800/api/register",
                {
                    username,
                    email,
                    password,
                    role,
                    dept_id: deptId, 
                    research_area: role === "professor" ? researchArea : undefined
                },
                { headers: { Authorization: `Bearer ${token}` } }
            );

            alert("‚úÖ User added successfully!");
            console.log(response.data);
            fetchUsers();
            resetForm();
        } catch (error) {
            console.error("‚ùå Error adding user:", error.response?.data || error);
            alert(error.response?.data?.message || "‚ùå Failed to add user.");
        }
    };

    // ‚úÖ Handle Updating a User
    const handleUpdateUser = async () => {
        if (!selectedUser) return;

        try {
            const token = localStorage.getItem("token");

            await axios.put(
                `http://localhost:8800/api/update/${selectedUser}`,
                { username, email, role },
                { headers: { Authorization: `Bearer ${token}` } }
            );

            alert("‚úÖ User updated successfully!");
            fetchUsers();
            resetForm();
        } catch (err) {
            console.error("‚ùå Error updating user:", err);
            alert("‚ùå Failed to update user.");
        }
    };

    // ‚úÖ Handle Deleting a User
    const handleDeleteUser = async (user_id) => {
        if (!window.confirm("Are you sure you want to delete this user?")) return;

        try {
            const token = localStorage.getItem("token");
            await axios.delete(`http://localhost:8800/api/delete/${user_id}`, {
                headers: { Authorization: `Bearer ${token}` }
            });

            alert("‚úÖ User deleted successfully!");
            fetchUsers();
        } catch (err) {
            console.error("‚ùå Error deleting user:", err);
            alert("‚ùå Failed to delete user.");
        }
    };

    // ‚úÖ Reset Form Fields
    const resetForm = () => {
        setUsername("");
        setEmail("");
        setRole("");
        setResearchArea("");
        setPassword("");
        setSelectedUser(null);
    };

    return (
        <div className="manage-users-container">

             {/* Top Header */}
                                                <div className="top-header">
                                                    <button className="back-button" onClick={() => navigate(-1)}>‚Üê Back</button>
                                                    <h1 className="university-name">Dharmshinh Desai University</h1>
                                                </div>
                                    
                                                {/* Sidebar */}
                                                <div className="sidebar">
                                                    <FaUserCircle size={50} className="user-icon" />
                                                    <p className="user-name">{userName}</p>
                                    
                                                    <div className="menu-options">
                                                        <button className="menu-button" onClick={() => navigate("/dashboard")}>Dashboard</button>
                                                        {(userRole === "hod" || userRole === "admin") && (
                                                            <button className="menu-button" onClick={() => navigate("/manage-users")}>Manage Users</button>
                                                        )}
                                                        {userRole === "admin" && (
                                                            <button className="menu-button" onClick={() => navigate("/manage-inventory")}>Manage Inventory</button>
                                                        )}
                                                        <button className="menu-button" onClick={() => navigate("/settings")}>‚öôÔ∏è Settings</button>
                                                    </div>
                                                </div>
            
                    <div></div>
            <h2>Manage Users</h2>

            {/* Add User Form */}
            <div className="user-form">
                <h3>Add New User</h3>
                <form onSubmit={handleAddUser}>
                    <input type="text" placeholder="Username" value={username} onChange={(e) => setUsername(e.target.value)} required />
                    <input type="email" placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} required />
                    <input type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} required />
                    <select value={role} onChange={(e) => setRole(e.target.value)} required>
                        <option value="">Select Role</option>
                        <option value="admin">Admin</option>
                        <option value="professor">Professor</option>
                    </select>
                    {role === "professor" && (
                        <input type="text" placeholder="Research Area (Optional)" value={researchArea} onChange={(e) => setResearchArea(e.target.value)} />
                    )}
                    <button type="submit">Add User</button>
                </form>
            </div>

            {/* User List */}
            <h3>Users List</h3>
            <table className="users-table">
                <thead>
                    <tr>
                        <th>Username</th>
                        <th>Email</th>
                        <th>Role</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {users.map((user) => (
                        <tr key={user.user_id}>
                            <td>{user.user_name}</td>
                            <td>{user.user_email}</td>
                            <td>{user.role}</td>
                            <td>
                                {/* üîπ Prevent editing or deleting self */}
                                {user.user_id !== userId && (
                                    <>
                                        <button onClick={() => { 
                                            setSelectedUser(user.user_id); 
                                            setUsername(user.user_name); 
                                            setEmail(user.user_email); 
                                            setRole(user.role); 
                                        }}>
                                            ‚úèÔ∏è Edit
                                        </button>
                                        <button onClick={() => handleDeleteUser(user.user_id)}>üóëÔ∏è Delete</button>
                                    </>
                                )}
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>

            {/* Update User Section */}
            {selectedUser && selectedUser !== userId && (
                <div className="user-form">
                    <h3>Update User</h3>
                    <input type="text" placeholder="Username" value={username} onChange={(e) => setUsername(e.target.value)} required />
                    <input type="email" placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} required />
                    <select value={role} onChange={(e) => setRole(e.target.value)} required>
                        <option value="admin">Admin</option>
                        <option value="professor">Professor</option>
                    </select>
                    <button onClick={handleUpdateUser}>Update User</button>
                </div>
            )}
        </div>
    );
};

export default ManageUsers;
