// export default Dashboard;
import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { FaUserCircle } from "react-icons/fa";
import { jwtDecode } from "jwt-decode";
import ManageUsers from "./ManageUsers";
import ManageInventory from "./ManageInventory";
import Settings from "./Settings";
import Profile from "./Profile";
import "./Dashboard.css";

const Dashboard = () => {
    const [faculties, setFaculties] = useState([]);
    const [userRole, setUserRole] = useState("");
    const [userName, setUserName] = useState(""); // ‚úÖ Store user name
    const [activeView, setActiveView] = useState("dashboard"); // ‚úÖ Track active section
    const navigate = useNavigate();

    useEffect(() => {
        const token = localStorage.getItem("token");
        
        if (token) {
            try {
                const decoded = jwtDecode(token);
                console.log("‚úÖ Decoded Token:", decoded);
                setUserRole(decoded.role);
                setUserName(decoded.user_name); // ‚úÖ Set user's name
            } catch (err) {
                console.error("‚ùå Error decoding token:", err);
                localStorage.removeItem("token");
                setUserRole("");
                setUserName("");
            }
        }
    }, []);

    useEffect(() => {
        const fetchAllFaculties = async () => {
            try {
                const res = await axios.get("http://localhost:8800/api/faculties");
                setFaculties(res.data);
            } catch (err) {
                console.error("‚ùå Error fetching faculties:", err);
            }
        };
        fetchAllFaculties();
    }, []);

    return (
        <div className="dashboard-container">
            {/* Sidebar */}
            <div className="sidebar">
                <FaUserCircle size={50} className="user-icon" />
                <p className="user-name">{userName || "User"}</p> {/* ‚úÖ Display User Name */}
                
                <div className="menu-options">
                    <button className={`menu-button ${activeView === "dashboard" ? "active" : ""}`} onClick={() => setActiveView("dashboard")}>
                        Dashboard
                    </button>

                    {/* ‚úÖ Show "Manage Users" for HODs & Admins */}
                    {(userRole === "hod" || userRole === "admin") && (
                        <button className={`menu-button ${activeView === "manage-users" ? "active" : ""}`} onClick={() => setActiveView("manage-users")}>
                            Manage Users
                        </button>
                    )}

                    {/* ‚úÖ Show "Manage Inventory" ONLY for Admins */}
                    {userRole === "admin" && (
                        <button className={`menu-button ${activeView === "manage-inventory" ? "active" : ""}`} onClick={() => setActiveView("manage-inventory")}>
                            Manage Inventory
                        </button>
                    )}

                    <button className={`menu-button ${activeView === "profile" ? "active" : ""}`} onClick={() => setActiveView("profile")}>
                        Profile
                    </button>

                    <button className={`menu-button ${activeView === "settings" ? "active" : ""}`} onClick={() => setActiveView("settings")}>
                        Settings
                    </button>

                    <button className="menu-button" onClick={() => {
                        localStorage.removeItem("token");
                        navigate("/");
                    }}>
                        Logout
                    </button>
                </div>
            </div>

            {/* Main Content */}
            <div className="main-content">
                {activeView === "dashboard" && (
                    <>
                        <div className="header">
                            <h1>DHARMSINH DESAI UNIVERSITY</h1>
                        </div>

                        {/* Faculties Section */}
                        <div className="faculties-grid">
                            {faculties
                                .filter(faculty => faculty.f_name.toLowerCase() !== "dduadmin") // ‚úÖ Hide "Admin" faculty
                                .map(faculty => (
                                    <div
                                        key={faculty.faculty_id}
                                        className="faculty-card"
                                        onClick={() => navigate(`/departments/${faculty.faculty_id}`)}
                                    >
                                        <div className="faculty-icon">üèõÔ∏è</div>
                                        <button className="faculty-name">{faculty.f_name}</button>
                                    </div>
                                ))
                            }
                        </div>
                    </>
                )}

                {activeView === "manage-users" && <ManageUsers />}
                {activeView === "manage-inventory" && <ManageInventory />}
                {activeView === "profile" && <Profile />}
                {activeView === "settings" && <Settings />}
            </div>
        </div>
    );
};

export default Dashboard;
