import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import './Departments.css';

const Departments = () => {
    const { faculty_id } = useParams();
    const [departments, setDepartments] = useState([]);
    const [selectedDepartmentId, setSelectedDepartmentId] = useState(null);
    const [rooms, setRooms] = useState([]);

    useEffect(() => {
        const fetchAllDepartments = async () => {
            try {
                const res = await axios.get(`http://localhost:8800/departments/${faculty_id}`);
                setDepartments(Array.isArray(res.data) ? res.data : []);
            } catch (err) {
                console.log(err);
                setDepartments([]);
            }
        };
        fetchAllDepartments();
    }, [faculty_id]);

    useEffect(() => {
        if (selectedDepartmentId) {
            const fetchRooms = async () => {
                try {
                    const res = await axios.get(`http://localhost:8800/rooms/${selectedDepartmentId}`);
                    setRooms(Array.isArray(res.data) ? res.data : []);
                } catch (err) {
                    console.log(err);
                    setRooms([]);
                }
            };
            fetchRooms();
        }
    }, [selectedDepartmentId]);

    // Count rooms based on type
    const classroomCount = rooms.filter(room => room.room_type === 'classroom').length;
    const labCount = rooms.filter(room => room.room_type === 'lab').length;
    const utilityCount = rooms.filter(room => room.room_type === 'utility').length;

    return (
        <div className="departments-container">
            {/* Header */}
            <div className="header">
                <h1>DHARMSINH DESAI UNIVERSITY</h1>
                <input type="text" placeholder="Search here..." className="search-bar" />
                <div className="profile-box"></div>
            </div>

            {/* Departments Navigation (Now Horizontal) */}
            <div className="departments-nav">
                {departments.map((dept) => (
                    <div
                        key={dept.dept_id}
                        className={`department-tab ${selectedDepartmentId === dept.dept_id ? 'active' : ''}`}
                        onClick={() => setSelectedDepartmentId(dept.dept_id)}
                    >
                        {dept.dept_name}
                    </div>
                ))}
            </div>

            {/* Department Details Section */}
            <div className="department-content">
                {selectedDepartmentId ? (
                    <div className="department-rooms">
                        <div className="department-card">
                            <h2>Classroom</h2>
                            <p>{classroomCount > 0 ? `${classroomCount} classrooms available.` : "No classrooms available."}</p>
                        </div>
                        <div className="department-card">
                            <h2>Lab</h2>
                            <p>{labCount > 0 ? `${labCount} labs available.` : "No labs available."}</p>
                        </div>
                        <div className="department-card">
                            <h2>Utility</h2>
                            <p>{utilityCount > 0 ? `${utilityCount} utilities available.` : "No ///utilities available."}</p>
                        </div>
                    </div>
                ) : (
                    <p>Select a department to view details.</p>
                )}
            </div>
        </div>
    );
};

export default Departments;
