import React, { useState, useEffect } from "react";
import { GoogleLogin } from "@react-oauth/google";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "./Register.css";

const Register = ({ onRegister, responseGoogle }) => {
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [confirmPassword, setConfirmPassword] = useState("");
    const [role, setRole] = useState("");  // Role dropdown state
    const [faculty, setFaculty] = useState(""); // Faculty dropdown state
    const [departments, setDepartments] = useState([]); // Department options
    const [department, setDepartment] = useState(""); // Selected department
    const clientId = process.env.REACT_APP_CLIENT_ID;
    const navigate = useNavigate();
    const [faculties, setFaculties] = useState([]); // Add this line


    // useEffect(() => {
    //     if (faculty) {
    //         // Fetch departments when faculty changes
    //         axios.get(`/api/departments?faculty=${faculty}`)

    //             .then(response => setDepartments(response.data))
    //             .catch(error => console.error("Error fetching departments:", error));
    //     } else {
    //         setDepartments([]); // Reset departments when no faculty is selected
    //     }
    // }, [faculty]);
    useEffect(() => {
        if (faculty) {
            axios.get(`/api/departments?faculty_id=${faculty}`)
                .then(response => setDepartments(response.data))
                .catch(error => console.error("Error fetching departments:", error));
        } else {
            setDepartments([]);
        }
    }, [faculty]);
    useEffect(() => {
        axios.get("http://localhost:8800/faculties") // Ensure this matches your backend URL
            .then(response => setFaculties(response.data))
            .catch(error => console.error("Error fetching faculties:", error));
    }, []);
    
    useEffect(() => {
        axios.get("/api/faculties")
            .then(response => setFaculty(response.data))
            .catch(error => console.error("Error fetching faculties:", error));
    }, []);

    useEffect(() => {
        if (faculty) {
            axios.get(`http://localhost:5000/api/departments?faculty_id=${faculty}`)
                .then(response => {
                    console.log("Departments fetched:", response.data); // Debugging log
                    setDepartments(response.data);
                })
                .catch(error => console.error("Error fetching departments:", error));
        } else {
            setDepartments([]); // Reset departments if no faculty is selected
        }
    }, [faculty]);
    
    

    const handleSubmit = (e) => {
        e.preventDefault();
        if (!email || !password || !confirmPassword || !role || !faculty || !department) {
            alert("Please fill in all fields");
            return;
        }
        if (!email.endsWith("@ddu.ac.in")) {
            alert("Only @ddu.ac.in emails are allowed.");
            return;
        }
        if (password !== confirmPassword) {
            alert("Passwords do not match");
            return;
        }

        onRegister({ email, password, role, faculty, department });
        navigate("/");
    };

    const handleGoogleRegister = (response) => {
        const credential = response.credential;
        const decodedToken = JSON.parse(atob(credential.split(".")[1])); // Decode JWT token
        const googleEmail = decodedToken.email;

        if (!googleEmail.endsWith("@ddu.ac.in")) {
            alert("Only @ddu.ac.in emails are allowed for registration.");
            return;
        }

        const handleSubmit = (e) => {
            e.preventDefault();
            if (!email || !password || !confirmPassword || !role || !faculty || !department) {
                alert("Please fill in all fields");
                return;
            }
            axios.post("/register", { email, password, role, faculty_id: faculty, department_id: department })
                .then(() => {
                    alert("Registration successful!");
                    navigate("/");
                })
                .catch(error => console.error("Registration failed:", error));
        };
        

        responseGoogle(response);
        navigate("/");
    };

    return (
        <div>
            <h2>Register</h2>
            <form onSubmit={handleSubmit} className="form-container">
                <div>
                    <label>Email:</label>
                    <input
                        type="email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        placeholder="Enter your email"
                    />
                </div>
                <div>
                    <label>Password:</label>
                    <input
                        type="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        placeholder="Enter your password"
                    />
                </div>
                <div>
                    <label>Confirm Password:</label>
                    <input
                        type="password"
                        value={confirmPassword}
                        onChange={(e) => setConfirmPassword(e.target.value)}
                        placeholder="Confirm your password"
                    />
                </div>
                <div>
                    <label>Role:</label>
                    <select value={role} onChange={(e) => setRole(e.target.value)}>
                        <option value="">Select Role</option>
                        <option value="admin">Admin</option>
                        <option value="teaching_staff">Teaching Staff</option>
                    </select>
                </div>
                <div>
                    <label>Faculty:</label>
                    <select value={faculty} onChange={(e) => setFaculty(e.target.value)}>
                        <option value="">Select Faculty</option>
                        {faculties.map((fac) => (
                            <option key={fac.id} value={fac.id}>{fac.name}</option> // Ensure fac.name is displayed
                        ))}
                    </select>
                </div>

                {faculty && (
                    <div>
                        <label>Department:</label>
                        <select value={department} onChange={(e) => setDepartment(e.target.value)}>
                            <option value="">Select Department</option>
                            {departments.map((dept) => (
                                <option key={dept.id} value={dept.id}>{dept.name}</option>
                            ))}
                        </select>
                    </div>
                )}

                <button type="submit">Register</button>
            </form>

            <GoogleLogin onSuccess={handleGoogleRegister} onError={() => console.log("Google Register Failed")} />

            <button className="login-button" onClick={() => navigate("/")}>
                Login
            </button>
        </div>
    );
};

export default Register;
