import React, { useEffect, useState, useContext } from "react";
import axios from "axios";
import { AuthContext } from "../auth/AuthContext";
import { useNavigate } from "react-router-dom";
import { FaUserCircle } from "react-icons/fa";

const ManageInventory = () => {
    const { deptId, userRole } = useContext(AuthContext);
    const [inventory, setInventory] = useState([]);
    const [selectedItem, setSelectedItem] = useState(null);
    const [itemName, setItemName] = useState("");
    const [itemId, setId] = useState("");
    const [status, setStatus] = useState("");
    const [quantity, setQuantity] = useState("");
    const [room_id, setRoomId] = useState("");
    const [category, setCategory] = useState("");
    const [lastMaintenance, setLastMaintenance] = useState("");
    const navigate = useNavigate();
    const [userName, setUserName] = useState(localStorage.getItem("userName") || "");

    useEffect(() => {
        fetchInventory();
    }, []);

    const fetchInventory = async () => {
        try {
            const token = localStorage.getItem("token");
            const response = await axios.get(`http://localhost:8800/api/inventory`, {
                headers: { Authorization: `Bearer ${token}` },
            });
            setInventory(response.data);
        } catch (error) {
            console.error("Error fetching inventory:", error);
        }
    };

    const handleAddItem = async (e) => {
        e.preventDefault();
        try {
            const token = localStorage.getItem("token");
            await axios.post("http://localhost:8800/api/inventory", { item_id: itemId, i_name: itemName, category, quantity, room_id, status, last_maintenance_date: lastMaintenance }, { headers: { Authorization: `Bearer ${token}` } });
            alert("Item added successfully!");
            fetchInventory();
            resetForm();
        } catch (error) {
            console.error("Error adding item:", error);
        }
    };

    const resetForm = () => {
        setItemName("");
        setCategory("");
        setQuantity("");
        setRoomId("");
        setStatus("");
        setLastMaintenance("");
        setSelectedItem(null);
    };

    return (
        <div style={{ padding: '20px', fontFamily: 'Arial' }}>
            <h2 style={{ marginBottom: '20px' }}>Manage Inventory</h2>
            <div>
                <h3>Add New Item</h3>
                <form onSubmit={handleAddItem} style={{ marginBottom: '20px' }}>
                    <input placeholder="Item Name" value={itemName} onChange={(e) => setItemName(e.target.value)} style={{ marginRight: '10px' }} />
                    <input placeholder="Category" value={category} onChange={(e) => setCategory(e.target.value)} style={{ marginRight: '10px' }} />
                    <input placeholder="Quantity" value={quantity} onChange={(e) => setQuantity(e.target.value)} style={{ marginRight: '10px' }} />
                    <input placeholder="Room ID" value={room_id} onChange={(e) => setRoomId(e.target.value)} style={{ marginRight: '10px' }} />
                    <input placeholder="Status" value={status} onChange={(e) => setStatus(e.target.value)} style={{ marginRight: '10px' }} />
                    <button type="submit">Add Item</button>
                </form>
            </div>
        </div>
    );
};

export default ManageInventory;
