// export default Dashboard;
import React from "react";
import { useNavigate } from "react-router-dom";
import { FaChalkboardTeacher, FaFlask, FaTools } from "react-icons/fa";
import "./Dashboard.css";

const Dashboard = () => {
    const navigate = useNavigate();

    const roomCategories = [
        { name: "Classrooms", icon: <FaChalkboardTeacher />, type: "classroom" },
        { name: "Labs", icon: <FaFlask />, type: "lab" },
        { name: "Utility Rooms", icon: <FaTools />, type: "utility" }
    ];

    return (
        <div className="dashboard-container">
            <h1 className="dashboard-title">University Inventory Management</h1>

            <div className="rooms-grid">
                {roomCategories.map((category) => (
                    <div 
                        key={category.type} 
                        className="room-card"
                        onClick={() => navigate(`/rooms/${category.type}`)}
                    >
                        <div className="room-icon">{category.icon}</div>
                        <h3>{category.name}</h3>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default Dashboard;
