import React, { useState } from "react";
import axios from "axios";

const AddUser = () => {
    const [username, setUsername] = useState("");
    const [email, setEmail] = useState("");
    const [role, setRole] = useState("");
    const [deptId, setDeptId] = useState("");
    const [password, setPassword] = useState(""); // ✅ Optional password entry

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            const response = await axios.post("http://localhost:8800/api/register", {
                username,
                email,
                password, // Default password (optional, will be hashed in backend)
                role,
                dept_id: deptId
            });

            alert("✅ User added successfully!");
            console.log(response.data);
        } catch (error) {
            console.error("❌ Error adding user:", error);
            alert("❌ Failed to add user.");
        }
    };

    return (
        <div>
            <h2>Add New User</h2>
            <form onSubmit={handleSubmit}>
                <input type="text" placeholder="Username" value={username} onChange={(e) => setUsername(e.target.value)} required />
                <input type="email" placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} required />
                <select value={role} onChange={(e) => setRole(e.target.value)} required>
                    <option value="">Select Role</option>
                    <option value="admin">Admin</option>
                    <option value="teaching_staff">Teaching Staff</option>
                    <option value="professor">Professor</option>
                </select>
                <input type="text" placeholder="Department ID" value={deptId} onChange={(e) => setDeptId(e.target.value)} required />
                <button type="submit">Add User</button>
            </form>
        </div>
    );
};

export default AddUser;
