import React, { useEffect, useState, useContext } from "react";
import axios from "axios";
import { AuthContext } from "../auth/AuthContext";
import { useNavigate } from "react-router-dom";
import { FaUserCircle } from "react-icons/fa";

const ManageInventory = () => {
    const { userRole } = useContext(AuthContext);
    const [inventory, setInventory] = useState([]);
    const [selectedItem, setSelectedItem] = useState(null);
    const [itemName, setItemName] = useState("");
    const [itemId, setItemId] = useState("");
    const [category, setCategory] = useState("");
    const [quantity, setQuantity] = useState("");
    const [roomId, setRoomId] = useState("");
    const [status, setStatus] = useState("");
    const [lastMaintenance, setLastMaintenance] = useState("");
    const navigate = useNavigate();
    const [userName, setUserName] = useState(localStorage.getItem("userName") || "");

    useEffect(() => {
        fetchInventory();
    }, []);

    const fetchInventory = async () => {
        try {
            const token = localStorage.getItem("token");
            const response = await axios.get("http://localhost:8800/api/inventory", {
                headers: { Authorization: `Bearer ${token}` },
            });
            setInventory(response.data);
        } catch (error) {
            console.error("‚ùå Error fetching inventory:", error);
        }
    };

    const handleSelectItem = (item) => {
        setSelectedItem(item);
        setItemName(item.i_name);
        setItemId(item.item_id);
        setCategory(item.category);
        setQuantity(item.quantity);
        setRoomId(item.room_id);
        setStatus(item.status);
        setLastMaintenance(item.last_maintenance_date);
    };

    const handleUpdateItem = async () => {
        try {
            const token = localStorage.getItem("token");
            await axios.put(`http://localhost:8800/api/inventory/${itemId}`, {
                i_name: itemName,
                category,
                quantity,
                room_id: roomId,
                status,
                last_maintenance_date: lastMaintenance,
            }, {
                headers: { Authorization: `Bearer ${token}` },
            });
            alert("‚úÖ Inventory item updated successfully!");
            fetchInventory();
            setSelectedItem(null);
        } catch (error) {
            console.error("‚ùå Error updating item:", error);
        }
    };

    const handleDeleteItem = async () => {
        if (!window.confirm("Are you sure you want to delete this item?")) return;
        try {
            const token = localStorage.getItem("token");
            await axios.delete(`http://localhost:8800/api/inventory/${itemId}`, {
                headers: { Authorization: `Bearer ${token}` },
            });
            alert("üóëÔ∏è Item deleted successfully!");
            fetchInventory();
            setSelectedItem(null);
        } catch (error) {
            console.error("‚ùå Error deleting item:", error);
        }
    };

    return (
        <>
            <style>{`
                .manage-inventory-container {
                    display: flex;
                    flex-direction: column;
                    align-items: center;
                    padding: 20px;
                    font-family: Arial, sans-serif;
                }
                .top-header {
                    display: flex;
                    justify-content: space-between;
                    align-items: center;
                    width: 100%;
                    background-color: #0056b3;
                    color: white;
                    padding: 10px;
                }
                .back-button {
                    background: none;
                    border: none;
                    color: white;
                    font-size: 16px;
                    cursor: pointer;
                }
                .sidebar {
                    display: flex;
                    flex-direction: column;
                    align-items: center;
                    background-color: #f4f4f4;
                    padding: 20px;
                    height: 100vh;
                    width: 200px;
                    position: fixed;
                    left: 0;
                    top: 0;
                }
                .menu-button {
                    background-color: #007bff;
                    color: white;
                    border: none;
                    padding: 10px;
                    margin: 5px;
                    cursor: pointer;
                    width: 100%;
                }
                .menu-button:hover {
                    background-color: #0056b3;
                }
                .inventory-table {
                    width: 80%;
                    border-collapse: collapse;
                    margin-top: 20px;
                }
                .inventory-table th, .inventory-table td {
                    border: 1px solid #ddd;
                    padding: 10px;
                    text-align: left;
                }
                .inventory-table th {
                    background-color: #007bff;
                    color: white;
                }
                .form-container {
                    margin-top: 20px;
                    padding: 15px;
                    border: 1px solid #ccc;
                    background-color: #f9f9f9;
                    width: 50%;
                }
                .form-container label {
                    display: block;
                    margin-top: 10px;
                }
                .form-container input {
                    width: 100%;
                    padding: 8px;
                    margin-top: 5px;
                }
                .form-container button {
                    margin-top: 10px;
                    padding: 10px;
                    background-color: #28a745;
                    color: white;
                    border: none;
                    cursor: pointer;
                }
                .delete-button {
                    background-color: #dc3545 !important;
                }
            `}</style>

            <div className="manage-inventory-container">
                <div className="top-header">
                    <button className="back-button" onClick={() => navigate(-1)}>‚Üê Back</button>
                    <h1 className="university-name">Dharmshinh Desai University</h1>
                </div>

                <div className="sidebar">
                    <FaUserCircle size={50} className="user-icon" />
                    <p className="user-name">{userName}</p>
                    <div className="menu-options">
                        <button className="menu-button" onClick={() => navigate("/dashboard")}>Dashboard</button>
                        {(userRole === "hod" || userRole === "admin") && (
                            <button className="menu-button" onClick={() => navigate("/manage-users")}>Manage Users</button>
                        )}
                        {userRole === "admin" && (
                            <button className="menu-button" onClick={() => navigate("/manage-inventory")}>Manage Inventory</button>
                        )}
                    </div>
                </div>

                <h2>Manage Inventory</h2>
                <table className="inventory-table">
                    <thead>
                        <tr>
                            <th>Item Name</th>
                            <th>Category</th>
                            <th>Quantity</th>
                            <th>Location</th>
                            <th>Status</th>
                            <th>Last Maintenance</th>
                        </tr>
                    </thead>
                    <tbody>
                        {inventory.map((item) => (
                            <tr key={item.item_id} onClick={() => handleSelectItem(item)}>
                                <td>{item.i_name}</td>
                                <td>{item.category}</td>
                                <td>{item.quantity}</td>
                                <td>{item.room_id}</td>
                                <td>{item.status}</td>
                                <td>{item.last_maintenance_date}</td>
                            </tr>
                        ))}
                    </tbody>
                </table>

                {selectedItem && (
                    <div className="form-container">
                        <h3>Edit Inventory</h3>
                        <label>Item Name:</label>
                        <input value={itemName} onChange={(e) => setItemName(e.target.value)} />
                        <label>Category:</label>
                        <input value={category} onChange={(e) => setCategory(e.target.value)} />
                        <label>Quantity:</label>
                        <input type="number" value={quantity} onChange={(e) => setQuantity(e.target.value)} />
                        <button onClick={handleUpdateItem}>Update</button>
                        <button className="delete-button" onClick={handleDeleteItem}>Delete</button>
                    </div>
                )}
            </div>
        </>
    );
};

export default ManageInventory;
