import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "./InventoryTable.css";
import { FaArrowLeft } from "react-icons/fa";

const InventoryTable = () => {
    const [inventory, setInventory] = useState([]);
    const navigate = useNavigate(); // Ensure this is defined at the top
    const userRole = localStorage.getItem("userRole");
    const userName = localStorage.getItem("userName");
    const userIcon = "path/to/user/icon.png"; 

    useEffect(() => {
        axios.get("/api/inventory")
            .then(response => setInventory(response.data))
            .catch(error => console.error("Error fetching inventory:", error));
    }, []);

    return (
        <div className="dashboard-container">
            {/* Sidebar */}
            <div className="sidebar">
                <div className="user-info">
                    <img src={userIcon} alt="User Icon" className="user-icon" />
                    <h2>{userName}</h2>
                </div>
                <button onClick={() => navigate("/dashboard")} className="menu-button">Dashboard</button>
                {userRole === "admin" && (
                    <button onClick={() => navigate("/manage-inventory")} className="menu-button">Manage Inventory</button>
                )}
                <button onClick={() => navigate("/logout")} className="menu-button">Logout</button>
            </div>

            {/* Main Content */}
            <div className="main-content">
                {/* Header */}
                <div className="top-header">
                    <button className="back-button" onClick={() => navigate(-1)}>
                        <FaArrowLeft />
                    </button>
                    <h1 className="university-name">Inventory Management</h1>
                </div>
                
                {/* Inventory Table */}
                <div className="table-container">
                    <table className="inventory-table">
                        <thead>
                            <tr>
                                <th>Item Name</th>
                                <th>Item ID</th>
                                <th>Category</th>
                                <th>Quantity</th>
                                <th>Location</th>
                                <th>Condition</th>
                                <th>Last Maintenance Date</th>
                            </tr>
                        </thead>
                        <tbody>
                            {inventory.map((item) => (
                                <tr key={item.item_id}>
                                    <td>{item.name}</td>
                                    <td>{item.item_id}</td>
                                    <td>{item.category}</td>
                                    <td>{item.quantity}</td>
                                    <td>{item.location}</td>
                                    <td>{item.condition}</td>
                                    <td>{item.last_maintenance_date}</td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            </div>
        </div>
    );
};

export default InventoryTable;
