import React, { useState, useEffect } from "react";
import axios from "axios";

const SuperAdminDashboard = () => {
    const [professors, setProfessors] = useState([]);
    const [selectedProfessor, setSelectedProfessor] = useState("");
    const [message, setMessage] = useState(""); // ‚úÖ Store messages for better feedback
    const [loading, setLoading] = useState(false); // ‚úÖ Loading state for button

    useEffect(() => {
        const fetchProfessors = async () => {
            try {
                const token = localStorage.getItem("token"); // ‚úÖ Get auth token
                const response = await axios.get("http://localhost:8800/api/professors", {
                    headers: { Authorization: `Bearer ${token}` }
                });

                console.log("‚úÖ Professors fetched:", response.data);
                setProfessors(response.data);
            } catch (error) {
                console.error("‚ùå Error fetching professors:", error);
                setMessage("Failed to load professors. Please try again.");
            }
        };

        fetchProfessors();
    }, []);

    const assignHOD = async () => {
        if (!selectedProfessor) {
            setMessage("‚ö†Ô∏è Please select a professor to assign as HOD.");
            return;
        }

        setLoading(true); // ‚úÖ Show loading state

        try {
            const token = localStorage.getItem("token"); // ‚úÖ Get token
            const response = await axios.put(
                "http://localhost:8800/api/superadmin/assign-hod",
                { user_id: selectedProfessor },
                { headers: { Authorization: `Bearer ${token}` } }
            );

            console.log("‚úÖ HOD assigned successfully:", response.data);
            setMessage("üéâ HOD assigned successfully!");
            setSelectedProfessor(""); // ‚úÖ Reset selection
        } catch (error) {
            console.error("‚ùå Error assigning HOD:", error.response ? error.response.data : error);
            setMessage("‚ùå Failed to assign HOD.");
        } finally {
            setLoading(false); // ‚úÖ Reset loading state
        }
    };

    return (
        <div>
            <h2>Super Admin Dashboard</h2>
            <h3>Assign Head of Department (HOD)</h3>

            {message && <p style={{ color: "red", fontWeight: "bold" }}>{message}</p>}

            <label>Select a Professor:</label>
            <select value={selectedProfessor} onChange={(e) => setSelectedProfessor(e.target.value)}>
                <option value="">Select Professor</option>
                {professors.map(prof => (
                    <option key={prof.user_id} value={prof.user_id}>
                        {prof.user_name} - {prof.user_email}
                    </option>
                ))}
            </select>

            <button onClick={assignHOD} disabled={loading || !selectedProfessor}>
                {loading ? "Assigning..." : "Assign as HOD"}
            </button>
        </div>
    );
};

export default SuperAdminDashboard;
