import React, { useEffect, useState, useContext } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "./Rooms.css";
import { AuthContext } from "../auth/AuthContext";

const Rooms = () => {
    const [rooms, setRooms] = useState([]);
    const navigate = useNavigate();
    const { deptId } = useContext(AuthContext); // ✅ Get deptId dynamically

    useEffect(() => {
        if (!deptId) return; // ✅ Avoid fetching if deptId is missing

        const fetchRooms = async () => {
            try {
                const token = localStorage.getItem("token");
                const response = await axios.get(`http://localhost:8800/api/rooms/${deptId}`, {
                    headers: { Authorization: `Bearer ${token}` },
                });
                setRooms(response.data);
            } catch (error) {
                console.error("❌ Error fetching rooms:", error);
            }
        };

        fetchRooms();
    }, [deptId]); // ✅ Fetch when deptId changes

    return (
        <div className="rooms-container">
            <h2>Rooms in Your Department</h2>

            <table className="rooms-table">
                <thead>
                    <tr>
                        <th>Room No.</th>
                        <th>Room ID</th>
                        <th>Capacity</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {rooms.map((room) => (
                        <tr key={room.room_id}>
                            <td>{room.room_num}</td>
                            <td>{room.room_id}</td>
                            <td>{room.capacity}</td>
                            <td>
                                <button onClick={() => navigate(`/inventory/${room.room_id}`)}>
                                    View Inventory
                                </button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default Rooms;
