import React, { useEffect, useState, useContext } from "react";
import axios from "axios";
import "./ManageInventory.css";
import { AuthContext } from "../auth/AuthContext";
import { useParams ,useNavigate} from "react-router-dom";
import { FaUserCircle } from "react-icons/fa";

const ManageInventory = () => {
    const { deptId, userRole } = useContext(AuthContext); // Get deptId from AuthContext
    const [inventory, setInventory] = useState([]);
    const [selectedItem, setSelectedItem] = useState(null);
    const [itemName, setItemName] = useState("");
    const [itemId, setId] = useState("");
    const [status, setStatus] = useState("");
    const [quantity, setQuantity] = useState("");
    const [room_id, setRoomId] = useState("");
    const [category, setCategory] = useState("");
    const [lastMaintenance, setLastMaintenance] = useState("");
    const navigate = useNavigate();  
    const [userName, setUserName] = useState(localStorage.getItem("userName") || "");
    

    useEffect(() => {
        fetchInventory();
    }, []);

    const fetchInventory = async () => {
        try {
            const token = localStorage.getItem("token");
            const response = await axios.get(`http://localhost:8800/api/inventory`, {
                headers: { Authorization: `Bearer ${token}` },
            });
            setInventory(response.data);
        } catch (error) {
            console.error("‚ùå Error fetching inventory:", error);
        }
    };

    const handleAddItem = async (e) => {
        e.preventDefault();
        try {
            const token = localStorage.getItem("token");
            await axios.post(
                "http://localhost:8800/api/inventory",
                {
                    item_id: itemId,
                    i_name: itemName,
                    category,
                    quantity,
                    room_id,
                    status,
                    last_maintenance_date: lastMaintenance,
                },
                { headers: { Authorization: `Bearer ${token}` } }
            );
            alert("‚úÖ Inventory item added successfully!");
            fetchInventory();
            resetForm();
        } catch (error) {
            console.error("‚ùå Error adding item:", error);
        }
    };

    const handleUpdateItem = async () => {
        if (!selectedItem) return;
        try {
            const token = localStorage.getItem("token");
            await axios.put(
                `http://localhost:8800/api/inventory/${selectedItem}`,
                {item_id: itemId, i_name: itemName, category, quantity, room_id, status, last_maintenance_date: lastMaintenance },
                { headers: { Authorization: `Bearer ${token}` } }
            );
            alert("‚úÖ Inventory item updated successfully!");
            fetchInventory();
            resetForm();
        } catch (error) {
            console.error("‚ùå Error updating item:", error);
        }
    };

    const handleDeleteItem = async (itemId) => {
        if (!window.confirm("Are you sure you want to delete this item?")) return;
        try {
            const token = localStorage.getItem("token");
            await axios.delete(`http://localhost:8800/api/inventory/${itemId}`, {
                headers: { Authorization: `Bearer ${token}` },
            });
            alert("‚úÖ Inventory item deleted successfully!");
            fetchInventory();
        } catch (error) {
            console.error("‚ùå Error deleting item:", error);
        }
    };

    const resetForm = () => {
        setItemName("");
        setCategory("");        
        setQuantity("");
        setRoomId("");
        setStatus("");
        setLastMaintenance("");
        setSelectedItem(null);
    };

    return (
        <div className="manage-inventory-container">
            {/* Top Header */}
                                    <div className="top-header">
                                        <button className="back-button" onClick={() => navigate(-1)}>‚Üê Back</button>
                                        <h1 className="university-name">Dharmshinh Desai University</h1>
                                    </div>
                        
                                    {/* Sidebar */}
                                    <div className="sidebar">
                                        <FaUserCircle size={50} className="user-icon" />
                                        <p className="user-name">{userName}</p>
                        
                                        <div className="menu-options">
                                            <button className="menu-button" onClick={() => navigate("/dashboard")}>Dashboard</button>
                                            {(userRole === "hod" || userRole === "admin") && (
                                                <button className="menu-button" onClick={() => navigate("/manage-users")}>Manage Users</button>
                                            )}
                                            {userRole === "admin" && (
                                                <button className="menu-button" onClick={() => navigate("/manage-inventory")}>Manage Inventory</button>
                                            )}
                                            <button className="menu-button" onClick={() => navigate("/settings")}>‚öôÔ∏è Settings</button>
                                        </div>
                                    </div>

        <div >
            <h2>Manage Inventory</h2>

            {userRole === "admin" && (
                <div className="inventory-form">
                    <h3>{selectedItem ? "Update Item" : "Add New Item"}</h3>
                    <form onSubmit={selectedItem ? handleUpdateItem : handleAddItem}>
                        <input type="text" placeholder="Item Id" value={itemId} onChange={(e) => setId(e.target.value)} required />
                        <input type="text" placeholder="Item Name" value={itemName} onChange={(e) => setItemName(e.target.value)} required />
                        <input type="text" placeholder="Category" value={category} onChange={(e) => setCategory(e.target.value)} required />
                        <input type="number" placeholder="Quantity" value={quantity} onChange={(e) => setQuantity(e.target.value)} required />
                        <input type="text" placeholder="RoomId" value={room_id} onChange={(e) => setRoomId(e.target.value)} required />
                        <input type="text" placeholder="Condition" value={status} onChange={(e) => setStatus(e.target.value)} required />
                        <input type="date" placeholder="Last Maintenance Date" value={lastMaintenance} onChange={(e) => setLastMaintenance(e.target.value)} />
                        <button type="submit">{selectedItem ? "Update Item" : "Add Item"}</button>
                    </form>
                </div>
            )}

            <h3>Inventory List</h3>
            <table className="inventory-table">
                <thead>
                    <tr>
                        <th>Item Name</th>
                        <th>Category</th>
                        <th>Quantity</th>
                        <th>Location</th>
                        <th>Status</th>
                        <th>Last Maintenance</th>
                        {userRole === "admin" && <th>Actions</th>}
                    </tr>
                </thead>
                <tbody>
                    {inventory.map((item) => (
                        <tr key={item.item_id}>
                            <td>{item.i_name}</td>
                            <td>{item.category}</td>
                            <td>{item.quantity}</td>
                            <td>{item.room_id}</td>
                            <td>{item.status}</td>
                            <td>{item.last_maintenance_date}</td>
                            {userRole === "admin" && (
                                <td>
                                    <button onClick={() => { 
                                        setSelectedItem(item.item_id); 
                                        setItemName(item.i_name);
                                        setCategory(item.category);
                                        setQuantity(item.quantity);
                                        setRoomId(item.room_id);
                                        setStatus(item.status);
                                        setLastMaintenance(item.last_maintenance_date);
                                    }}>‚úèÔ∏è Edit</button>
                                    <button onClick={() => handleDeleteItem(item.item_id)}>üóëÔ∏è Delete</button>
                                </td>
                            )}
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
        
        </div>
    );
};

export default ManageInventory;
