// SidebarInventoryAlerts.jsx
import { useEffect, useState } from "react";

const SidebarInventoryAlerts = () => {
  const [oldItems, setOldItems] = useState([]);
  const [error, setError] = useState("");
  const [refresh, setRefresh] = useState(false);


  useEffect(() => {
    const fetchInventory = async () => {
      try {
        const token = localStorage.getItem("token");
        const res = await fetch("http://localhost:8800/api/inventory", {
          headers: { Authorization: `Bearer ${token}` },
        });
        if (!res.ok) throw new Error("Failed to fetch inventory.");
        const data = await res.json();
        console.log(data);

        const now = new Date();
        const filtered = data.filter(item => {
          if (!item.last_maintenance_date) return false;
          const last = new Date(item.last_maintenance_date);
          const diffDays = (now - last) / (1000 * 60 * 60 * 24);
          return diffDays > 30;
        });

        setOldItems(filtered);
      } catch (err) {
        setError("Couldn't load maintenance alerts.");
      }
    };

    fetchInventory();
  }, [refresh]);


  const handleUpdateDate = async (item) => {
    const role = localStorage.getItem("role");
  
    // ‚úÖ Block HODs and unauthorized roles
    if (role !== "admin") {
      alert("‚ùå Only admins can update maintenance dates.");
      return;
    }
  
    const confirmed = window.confirm(
      `Mark "${item.i_name}" (Room ${item.room_id}) as maintained today?`
    );
    if (!confirmed) return;
  
    try {
      const token = localStorage.getItem("token");
      const today = new Date().toISOString().split("T")[0]; // Format: YYYY-MM-DD
  
      const res = await fetch(`http://localhost:8800/api/inventory/${item.item_id}`, {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({
          i_name: item.i_name,
          category: item.category,
          quantity: item.quantity,
          room_id: item.room_id,
          condition: "up to date",
          last_maintenance_date: today,
        }),
      });
  
      if (!res.ok) throw new Error("Failed to update item.");
      setRefresh(prev => !prev); // Trigger re-fetch
      alert("‚úÖ Maintenance date and status updated successfully.");
    } catch (err) {
      console.error(err);
      alert("‚ùå Failed to update maintenance date.");
    }
  };
  


  return (
    <div style={{ 
      marginTop: "30px",
      width: "100%",
      maxHeight: "300px",
      overflowY: "auto"
    }}>
      <h4 style={{ color: "#fff", marginBottom: "10px" }}>üõ†Ô∏è Maintenance Alerts</h4>

      {error ? (
        <p style={{ color: "#f87171", fontSize: "14px" }}>{error}</p>
      ) : oldItems.length === 0 ? (
        <p style={{ color: "#9CA3AF", fontSize: "14px" }}>No pending items</p>
      ) : (
        <ul style={{ listStyle: "none", padding: 0, margin: 0 }}>
    {oldItems.map(item => (
      <li 
        key={item.item_id}
        onClick={() => handleUpdateDate(item)} 
        style={{
          backgroundColor: "#ffffff",
          color: "#000000",
          padding: "8px",
          borderRadius: "5px",
          marginBottom: "6px",
          fontSize: "13px",
          cursor: "pointer",
          transition: "background 0.3s"
        }}
        onMouseOver={e => (e.currentTarget.style.backgroundColor = "#e5e7eb")}
              onMouseOut={e => (e.currentTarget.style.backgroundColor = "#ffffff")}
              title="Click to mark as maintained"
        >
        {item.i_name} (Room {item.room_id})
      </li>
    ))}
  </ul>
      )}
    </div>
  );
};

export default SidebarInventoryAlerts;
