import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "./Login.css";

const Login = ({ setUserId }) => {
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const navigate = useNavigate();

    const handleSubmit = async (e) => {
        e.preventDefault();
    
        try {
            const response = await axios.post("http://localhost:8800/api/login", {
                email,
                password
            });
    
            console.log("‚úÖ Backend Response:", response.data); // Debugging line
    
            const { token, user } = response.data;
            if (!token) {
                console.error("‚ùå No token received from backend!");
                return alert("‚ùå Authentication failed! No token received.");
            }
    
            // ‚úÖ Store token in localStorage
            localStorage.setItem("google_token", token);
    
            // ‚úÖ Debugging: Check stored token
            console.log("üõ† Stored Token:", localStorage.getItem("google_token"));
    
            setUserId(user.user_id);
            if (user.role.toLowerCase() === "super_admin") navigate("/superadmin");
            else navigate("/dashboard");
    
        } catch (error) {
            console.error("‚ùå Login failed:", error.response ? error.response.data : error);
            alert("‚ùå Invalid email or password.");
        }
    };
    
    return (
        <div>
            <h2>Login</h2>
            <form onSubmit={handleSubmit} className="form-container">
                <div>
                    <label>Email:</label>
                    <input
                        type="email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        placeholder="Enter your email"
                        required
                    />
                </div>
                <div>
                    <label>Password:</label>
                    <input
                        type="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        placeholder="Enter your password"
                        required
                    />
                </div>
                <button type="submit">Login</button>
            </form>
        </div>
    );
};

export default Login;
