import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "./Register.css";

const Register = ({ onRegister }) => {
    const [username, setUsername] = useState(""); // ✅ Added Username State
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [confirmPassword, setConfirmPassword] = useState("");
    const [role, setRole] = useState("");
    const [faculty, setFaculty] = useState("");
    const [faculties, setFaculties] = useState([]);
    const [departments, setDepartments] = useState([]);
    const [department, setDepartment] = useState("");
    const navigate = useNavigate();

    // ✅ Fetch Faculties on Page Load
    useEffect(() => {
        axios.get("http://localhost:8800/api/faculties")
            .then(response => {
                console.log("Faculties fetched:", response.data);
                setFaculties(response.data);
            })
            .catch(error => console.error("Error fetching faculties:", error));
    }, []);

    // ✅ Fetch Departments When Faculty is Selected
    useEffect(() => {
        if (faculty) {
            axios.get(`http://localhost:8800/api/departments/${faculty}`)
                .then(response => {
                    console.log("Departments fetched:", response.data);
                    setDepartments(response.data);
                })
                .catch(error => console.error("Error fetching departments:", error));
        } else {
            setDepartments([]);
        }
    }, [faculty]);

    // ✅ Handle Form Submission
    // const handleSubmit = (e) => {
    //     e.preventDefault();
    //     if (!username || !email || !password || !confirmPassword || !role || !faculty || !department) {
    //         alert("Please fill in all fields");
    //         return;
    //     }
    //     if (password !== confirmPassword) {
    //         alert("Passwords do not match");
    //         return;
    //     }

    //     axios.post("http://localhost:8800/api/register", {
    //         username, // ✅ Added username field to API request
    //         email,
    //         password,
    //         role,
    //         faculty_id: faculty,
    //         department_id: department
    //     })
    //     .then(() => {
    //         alert("Registration successful!");
    //         navigate("/");
    //     })
    //     .catch(error => console.error("Registration failed:", error));
    // };

    const handleSubmit = async (e) => {
        e.preventDefault(); // ✅ Prevent form from refreshing the page
    
        // ✅ Debugging log
        console.log("Submitting form...");
    
        if (!username || !email || !password || !confirmPassword || !role || !faculty || !department) {
            alert("Please fill in all fields");
            return;
        }
        if (password !== confirmPassword) {
            alert("Passwords do not match");
            return;
        }
    
        try {
            console.log("Sending request to backend...");
    
            const response = await axios.post("http://localhost:8800/api/register", {
                username,
                email,
                password,
                role,
                faculty_id: faculty,
                department_id: department
            });
    
            console.log("Registration successful:", response.data);
    
            alert("Registration successful!");
            
            // ✅ Redirect to Dashboard after successful registration
            navigate("/dashboard");
            
        } catch (error) {
            console.error("Registration failed:", error);
            alert("Registration failed. Please try again.");
        }
    };
    

    return (
        <div>
            <h2>Register</h2>
            <form onSubmit={handleSubmit} className="form-container">
                <div>
                    <label>Username:</label>
                    <input
                        type="text"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                        placeholder="Enter your username"
                    />
                </div>
                <div>
                    <label>Email:</label>
                    <input
                        type="email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        placeholder="Enter your email"
                    />
                </div>
                <div>
                    <label>Password:</label>
                    <input
                        type="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        placeholder="Enter your password"
                    />
                </div>
                <div>
                    <label>Confirm Password:</label>
                    <input
                        type="password"
                        value={confirmPassword}
                        onChange={(e) => setConfirmPassword(e.target.value)}
                        placeholder="Confirm your password"
                    />
                </div>
                <div>
                    <label>Role:</label>
                    <select value={role} onChange={(e) => setRole(e.target.value)}>
                        <option value="">Select Role</option>
                        <option value="admin">Admin</option>
                        <option value="teaching_staff">Teaching Staff</option>
                    </select>
                </div>
                <div>
                    <label>Faculty:</label>
                    <select value={faculty} onChange={(e) => setFaculty(e.target.value)}>
                        <option value="">Select Faculty</option>
                        {faculties.map((fac) => (
                            <option key={fac.faculty_id} value={fac.faculty_id}>{fac.f_name}</option>
                        ))}
                    </select>
                </div>
                {faculty && (
                    <div>
                        <label>Department:</label>
                        <select value={department} onChange={(e) => setDepartment(e.target.value)}>
                            <option value="">Select Department</option>
                            {departments.map((dept) => (
                                <option key={dept.dept_id} value={dept.dept_id}>{dept.dept_name}</option>
                            ))}
                        </select>
                    </div>
                )}
                <button type="submit">Register</button>
            </form>
        </div>
    );
};

export default Register;
