import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Rooms from './Rooms'; // Import the Rooms component
import './Departments.css';

const Departments = ({ selectedFaculty }) => {
    const [departments, setDepartments] = useState([]);
    const [selectedDepartment, setSelectedDepartment] = useState(null);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        if (selectedFaculty) {
            fetchDepartments(selectedFaculty);
        }
    }, [selectedFaculty]);

    const fetchDepartments = async (facultyId) => {
        try {
            setLoading(true);
            const res = await axios.get(`http://localhost:8800/departments/${facultyId}`);
            setDepartments(res.data);
        } catch (error) {
            console.error('Error fetching departments:', error);
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="departments-container">
            <h2>Departments</h2>
            <div className="departments-nav">
                {loading ? (
                    <p>Loading departments...</p>
                ) : departments.length > 0 ? (
                    departments.map((department) => (
                        <div 
                            key={department.department_id} 
                            className={`department-card ${selectedDepartment === department.department_id ? 'active' : ''}`}
                            onClick={() => setSelectedDepartment(department.department_id)}
                        >
                            <div className="department-icon">üè´</div>
                            <h3>{department.d_name}</h3>
                            <p>Head: {department.hod_name}</p>

                            {/* Show Rooms for the Selected Department */}
                            {selectedDepartment === department.department_id && <Rooms departmentId={department.department_id} />}
                        </div>
                    ))
                ) : (
                    <p>No departments found.</p>
                )}
            </div>
        </div>
    );
};

export default Departments;
