import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './Rooms.css'; 

const Rooms = ({ departmentId }) => {
    const [rooms, setRooms] = useState([]);

    useEffect(() => {
        if (departmentId) {
            axios.get(`http://localhost:8800/rooms/${departmentId}`)
                .then(response => setRooms(response.data))
                .catch(error => console.error("Error fetching rooms:", error));
        }
    }, [departmentId]);

    // Categorize rooms
    const classroomRooms = rooms.filter(room => room.type === 'classroom');
    const labRooms = rooms.filter(room => room.type === 'lab');
    const utilityRooms = rooms.filter(room => room.type === 'utility');

    return (
        <div className="rooms-container">
            <h3>Room Details</h3>
            <div className="room-list">
                {/* Classroom Card */}
                <div className="room-card">
                    <h4>üìö Classrooms</h4>
                    {classroomRooms.length > 0 ? (
                        classroomRooms.map(room => <p key={room.id}>{room.name}</p>)
                    ) : <p>No classrooms available.</p>}
                </div>

                {/* Lab Card */}
                <div className="room-card">
                    <h4>üî¨ Labs</h4>
                    {labRooms.length > 0 ? (
                        labRooms.map(room => <p key={room.id}>{room.name}</p>)
                    ) : <p>No labs available.</p>}
                </div>

                {/* Utility Room Card */}
                <div className="room-card">
                    <h4>üõ†Ô∏è Utility Rooms</h4>
                    {utilityRooms.length > 0 ? (
                        utilityRooms.map(room => <p key={room.id}>{room.name}</p>)
                    ) : <p>No utility rooms available.</p>}
                </div>
            </div>
        </div>
    );
};

export default Rooms;
