import React, { useEffect, useState, useContext } from "react";
import { useNavigate, useParams } from "react-router-dom";
import axios from "axios";
import "./Rooms.css";
import { AuthContext } from "../auth/AuthContext";


const Rooms = () => {
    const { roomType } = useParams(); // Get the selected category (classroom, lab, utility)
    const [rooms, setRooms] = useState([]);
    const navigate = useNavigate();
    const { deptId } = useContext(AuthContext); // Get department ID from AuthContext

    useEffect(() => {
        if (!deptId) return;
    
        const fetchRooms = async () => {
            try {
                const token = localStorage.getItem("token");
                const response = await axios.get(`http://localhost:8800/api/rooms/${deptId}/${roomType}`, {
                    headers: { Authorization: `Bearer ${token}` },
                });
                setRooms(response.data);
            } catch (error) {
                console.error("❌ Error fetching rooms:", error);
            }
        };
    
        fetchRooms();
    }, [deptId, roomType]);
    // Re-fetch when deptId or roomType changes

    return (
        <div className="rooms-container">
        <h2>{roomType ? roomType.charAt(0).toUpperCase() + roomType.slice(1) : "Unknown Category"} List</h2>

            <table className="rooms-table">
                <thead>
                    <tr>
                        <th>Room No.</th>
                        <th>Room ID</th>
                        <th>Capacity</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {rooms?.length > 0 ? (
                        rooms.map((room) => (
                            <tr key={room.room_id}>
                                <td>{room.room_num}</td>
                                <td>{room.room_id}</td>
                                <td>{room.capacity}</td>
                                <td>
                                    <button onClick={() => navigate(`/inventory/${room.room_id}`)}>View Inventory</button>
                                </td>
                            </tr>
                        ))
                    ) : (
                        <tr>
                            <td colSpan="4">No rooms available.</td>
                        </tr>
                    )}
                </tbody>

            </table>
        </div>
    );
};

export default Rooms;
