import React, { useEffect, useState, useContext } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "./Rooms.css";
import { AuthContext } from "../auth/AuthContext";

const Rooms = () => {
    const [classrooms, setClassrooms] = useState([]);
    const [labs, setLabs] = useState([]);
    const [utilities, setUtilities] = useState([]);
    const navigate = useNavigate();
    const { deptId } = useContext(AuthContext);

    useEffect(() => {
        if (!deptId) return; // ‚úÖ Avoid fetching if deptId is missing

        const fetchRooms = async (roomType, setRooms) => {
            try {
                const token = localStorage.getItem("token");
                const response = await axios.get(`http://localhost:8800/api/rooms/${deptId}/${roomType}`, {
                    headers: { Authorization: `Bearer ${token}` },
                });
                setRooms(response.data);
            } catch (error) {
                console.error(`‚ùå Error fetching ${roomType} rooms:`, error);
            }
        };

        fetchRooms("classroom", setClassrooms);
        fetchRooms("lab", setLabs);
        fetchRooms("utility", setUtilities);
    }, [deptId]);

    return (
        <div className="rooms-container">
            <h2>Rooms in Your Department</h2>

            {/* ‚úÖ Classrooms Table */}
            <h3>üìö Classrooms</h3>
            <table className="rooms-table">
                <thead>
                    <tr>
                        <th>Room No.</th>
                        <th>Room ID</th>
                        <th>Capacity</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {classrooms.length > 0 ? (
                        classrooms.map((room) => (
                            <tr key={room.room_id}>
                                <td>{room.room_num}</td>
                                <td>{room.room_id}</td>
                                <td>{room.capacity}</td>
                                <td>
                                    <button onClick={() => navigate(`/inventory/${room.room_id}`)}>
                                        View Inventory
                                    </button>
                                </td>
                            </tr>
                        ))
                    ) : (
                        <tr>
                            <td colSpan="4">No classrooms available.</td>
                        </tr>
                    )}
                </tbody>
            </table>

            {/* ‚úÖ Labs Table */}
            <h3>üî¨ Labs</h3>
            <table className="rooms-table">
                <thead>
                    <tr>
                        <th>Room No.</th>
                        <th>Room ID</th>
                        <th>Capacity</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {labs.length > 0 ? (
                        labs.map((room) => (
                            <tr key={room.room_id}>
                                <td>{room.room_num}</td>
                                <td>{room.room_id}</td>
                                <td>{room.capacity}</td>
                                <td>
                                    <button onClick={() => navigate(`/inventory/${room.room_id}`)}>
                                        View Inventory
                                    </button>
                                </td>
                            </tr>
                        ))
                    ) : (
                        <tr>
                            <td colSpan="4">No labs available.</td>
                        </tr>
                    )}
                </tbody>
            </table>

            {/* ‚úÖ Utility Rooms Table */}
            <h3>üõ†Ô∏è Utility Rooms</h3>
            <table className="rooms-table">
                <thead>
                    <tr>
                        <th>Room No.</th>
                        <th>Room ID</th>
                        <th>Capacity</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {utilities.length > 0 ? (
                        utilities.map((room) => (
                            <tr key={room.room_id}>
                                <td>{room.room_num}</td>
                                <td>{room.room_id}</td>
                                <td>{room.capacity}</td>
                                <td>
                                    <button onClick={() => navigate(`/inventory/${room.room_id}`)}>
                                        View Inventory
                                    </button>
                                </td>
                            </tr>
                        ))
                    ) : (
                        <tr>
                            <td colSpan="4">No utility rooms available.</td>
                        </tr>
                    )}
                </tbody>
            </table>
        </div>
    );
};

export default Rooms;
