import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "./Register.css";

const Register = ({ onRegister }) => {
    const [username, setUsername] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [confirmPassword, setConfirmPassword] = useState("");
    const [role, setRole] = useState("");
    const [faculty, setFaculty] = useState(""); // ‚úÖ Stores selected faculty_id
    const [faculties, setFaculties] = useState([]); // ‚úÖ Stores list of faculties
    const [department, setDepartment] = useState(""); // ‚úÖ Stores selected department_id
    const [departments, setDepartments] = useState([]); // ‚úÖ Stores list of departments
    const navigate = useNavigate();

    // ‚úÖ Fetch Faculties on Page Load
    useEffect(() => {
        axios.get("http://localhost:8800/api/faculties")
            .then(response => {
                console.log("Faculties fetched:", response.data);
                setFaculties(response.data);
            })
            .catch(error => console.error("Error fetching faculties:", error));
    }, []);

    // ‚úÖ Fetch Departments When Faculty is Selected
    useEffect(() => {
        if (faculty) {
            axios.get(`http://localhost:8800/api/departments/${faculty}`)
                .then(response => {
                    console.log("Departments fetched:", response.data);
                    setDepartments(response.data);
                })
                .catch(error => console.error("Error fetching departments:", error));
        } else {
            setDepartments([]);
        }
    }, [faculty]);

    // ‚úÖ Handle Form Submission
    const handleSubmit = async (e) => {
        e.preventDefault();
    
        console.log("üîπ Submitting form...");

        if (!username || !email || !password || !confirmPassword || !role || !faculty || !department) {
            alert("‚ùå Please fill in all fields");
            return;
        }
        if (password !== confirmPassword) {
            alert("‚ùå Passwords do not match");
            return;
        }
    
        try {
            console.log("üîπ Sending request to backend with data:", {
                username,
                email,
                password,
                role,
                faculty_id: faculty, // ‚úÖ Sending selected faculty_id
                dep_id: department // ‚úÖ Sending selected department_id
            });

            const response = await axios.post("http://localhost:8800/api/register", {
                username,
                email,
                password,
                role,
                faculty_id: faculty, // ‚úÖ Corrected faculty_id
                dept_id: department // ‚úÖ Corrected department_id
            });

            console.log("‚úÖ Registration successful:", response.data);
            alert("üéâ Registration successful!");
            navigate("/dashboard");

        } catch (error) {
            console.error("‚ùå Registration failed:", error.response ? error.response.data : error);
            alert("‚ùå Registration failed. Check console for details.");
        }
    };

    return (
        <div>
            <h2>Register</h2>
            <form onSubmit={handleSubmit} className="form-container">
                <div>
                    <label>Username:</label>
                    <input
                        type="text"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                        placeholder="Enter your username"
                    />
                </div>
                <div>
                    <label>Email:</label>
                    <input
                        type="email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        placeholder="Enter your email"
                    />
                </div>
                <div>
                    <label>Password:</label>
                    <input
                        type="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        placeholder="Enter your password"
                    />
                </div>
                <div>
                    <label>Confirm Password:</label>
                    <input
                        type="password"
                        value={confirmPassword}
                        onChange={(e) => setConfirmPassword(e.target.value)}
                        placeholder="Confirm your password"
                    />
                </div>
                <div>
                    <label>Role:</label>
                    <select value={role} onChange={(e) => setRole(e.target.value)}>
                        <option value="">Select Role</option>
                        <option value="admin">Admin</option>
                        <option value="teaching_staff">Teaching Staff</option>
                    </select>
                </div>
                <div>
                    <label>Faculty:</label>
                    <select value={faculty} onChange={(e) => setFaculty(e.target.value)}>
                        <option value="">Select Faculty</option>
                        {faculties.map((fac) => (
                            <option key={fac.faculty_id} value={fac.faculty_id}>{fac.f_name}</option>
                        ))}
                    </select>
                </div>
                {faculty && (
                    <div>
                        <label>Department:</label>
                        <select value={department} onChange={(e) => setDepartment(e.target.value)}>
                            <option value="">Select Department</option>
                            {departments.map((dept) => (
                                <option key={dept.dept_id} value={dept.dept_id}>{dept.dept_name}</option>
                            ))}
                        </select>
                    </div>
                )}
                <button type="submit">Register</button>
            </form>
        </div>
    );
};

export default Register;
