import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { FaUserCircle } from "react-icons/fa";
import "./Dashboard.css";

const Dashboard = () => {
    const [faculties, setFaculties] = useState([]);
    const [userRole, setUserRole] = useState(localStorage.getItem("userRole") || "");
    const [userName, setUserName] = useState(localStorage.getItem("userName") || ""); 
    const navigate = useNavigate();

    useEffect(() => {
        const fetchAllFaculties = async () => {
            try {
                const res = await axios.get("http://localhost:8800/api/faculties");
                setFaculties(res.data);
            } catch (err) {
                console.error("❌ Error fetching faculties:", err);
            }
        };
        fetchAllFaculties();
    }, []);

    return (
        <div className="dashboard-container">
            {/* Sidebar */}
            <div className="sidebar">
                <FaUserCircle size={50} className="user-icon" />
                <p className="user-name">{userName}</p> {/* Display user name */}
                <div className="menu-options">
                    <button className="menu-button" onClick={() => navigate("/dashboard")}>Dashboard</button>

                    {/* Show "Manage Users" for HODs & Admins */}
                    {(userRole === "hod" || userRole === "admin") && (
                        <button className="menu-button" onClick={() => navigate("/manage-users")}>
                            Manage Users
                        </button>
                    )}

                    {/* Show "Manage Inventory" ONLY for Admins */}
                    {userRole === "admin" && (
                        <button className="menu-button" onClick={() => navigate("/manage-inventory")}>
                            Manage Inventory
                        </button>
                    )}

                    <button className="menu-button" onClick={() => navigate("/profile")}>Profile</button>
                    <button className="menu-button" onClick={() => navigate("/settings")}>Settings</button>
                    <button className="menu-button" onClick={() => {
                        localStorage.removeItem("token");
                        localStorage.removeItem("userRole");
                        localStorage.removeItem("userName");
                        navigate("/");
                    }}>Logout</button>
                </div>
            </div>

            {/* Main Content */}
            <div className="main-content">
                <div className="header">
                    <h1>DHARMSINH DESAI UNIVERSITY</h1>
                </div>

                {/* Faculties Section */}
                <div className="faculties-grid">
                    {faculties
                        .filter(faculty => faculty.f_name.toLowerCase() !== "dduadmin") 
                        .map(faculty => (
                            <div
                                key={faculty.faculty_id}
                                className="faculty-card"
                                onClick={() => navigate(`/departments/${faculty.faculty_id}`)}
                            >
                                <div className="faculty-icon">🏛️</div>
                                <button className="faculty-name">{faculty.f_name}</button>
                            </div>
                        ))
                    }
                </div>
            </div>
        </div>
    );
};

export default Dashboard;
