import { BrowserRouter, Routes, Route, useNavigate } from "react-router-dom";
import "./App.css";
import { useState, useEffect } from "react";
import { jwtDecode } from "jwt-decode";
import { GoogleOAuthProvider } from "@react-oauth/google";
import axios from "axios";

import Dashboard from "./pages/Dashboard";
import SuperAdminDashboard from "./pages/SuperAdminDashboard";
import Departments from "./pages/Departments";
import Update from "./pages/Update";
import Add from "./pages/Add";
import Login from "./pages/Login";
import ChangePassword from "./pages/ChangePassword";

const clientId = process.env.REACT_APP_CLIENT_ID;

function App() {
    const [user, setUser] = useState(null);
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const [userId, setUserId] = useState(""); // ✅ Store user_id for password change
    const navigate = useNavigate();

    useEffect(() => {
        const storedToken = window.localStorage.getItem("google_token");
        if (storedToken) {
            const decodedUser = jwtDecode(storedToken);
            setUser(decodedUser);
        }
    }, []);

    const responseGoogle = (res) => {
        if (res?.credential) {
            const decodedUser = jwtDecode(res.credential);
            setUser(decodedUser);
            window.localStorage.setItem("google_token", res.credential);
        } else {
            setUser(null);
            window.localStorage.removeItem("google_token");
        }
    };

    // ✅ FIX: Handle Login with Backend & Redirect Based on Role
    const handleLogin = (credentials) => {
        axios.post("http://localhost:8800/api/login", credentials)
            .then(response => {
                setIsLoggedIn(true);
                setUserId(response.data.user.user_id); // ✅ Store user_id for password change
                alert("Login successful!");

                const userRole = response.data.user.role; // ✅ Get user role

                if (userRole === "super_admin") {
                    navigate("/superadmin"); // ✅ Redirect super admin
                } else {
                    navigate("/dashboard"); // ✅ Redirect other users
                }
            })
            .catch(error => {
                console.error("❌ Login error:", error);
                alert("❌ Invalid email or password.");
            });
    };

    return (
        <GoogleOAuthProvider clientId={clientId}>
            <BrowserRouter>
                <div className="App">
                    <header className="App-header">
                        <Routes>
                            <Route path="/dashboard" element={<Dashboard />} />
                            <Route path="/superadmin" element={<SuperAdminDashboard />} />
                            <Route path="/add" element={<Add />} />
                            <Route path="/update/:id" element={<Update />} />
                            <Route path="/departments/:faculty_id" element={<Departments />} />
                            <Route path="/changepassword" element={<ChangePassword user_id={userId} />} />
                            <Route path="/" element={<Login onLogin={handleLogin} responseGoogle={responseGoogle} />} />
                        </Routes>
                    </header>
                </div>
            </BrowserRouter>
        </GoogleOAuthProvider>
    );
}

export default App;
