import { createContext, useState, useEffect } from "react";
import { jwtDecode } from "jwt-decode";

export const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
    const [user, setUser] = useState(null);
    const [userId, setUserId] = useState("");
    const [deptId, setDeptId] = useState("");  // üîπ Store dept_id
    const [userRole, setUserRole] = useState(""); // üîπ Store role

    useEffect(() => {
        try {
            const storedToken = window.localStorage.getItem("token"); // ‚úÖ Use correct token key

            if (storedToken && storedToken.split(".").length === 3) {
                const decodedUser = jwtDecode(storedToken);
                setUser(decodedUser);
                setUserId(decodedUser.user_id || "");
                setDeptId(decodedUser.dept_id || ""); 
                setUserRole(decodedUser.role || "");

                if (!decodedUser.dept_id) {
                    console.warn("‚ö†Ô∏è Warning: `dept_id` missing in token.");
                }
            } else {
                console.warn("‚ö†Ô∏è Invalid or missing token. Logging out.");
                localStorage.removeItem("token"); 
                setUser(null);
                setUserId("");
                setDeptId("");
                setUserRole("");
            }
        } catch (error) {
            console.error("‚ùå Error decoding token:", error);
            window.localStorage.removeItem("token");
            setUser(null);
            setUserId("");
            setDeptId("");
            setUserRole("");
        }
    }, []);

    return (
        <AuthContext.Provider value={{ user, setUser, userId, setUserId, deptId, userRole }}>
            {children}
        </AuthContext.Provider>
    );
};
