import React, { useEffect, useState } from "react";
import axios from "axios";
import "./ManageUsers.css";

const ManageUsers = () => {
    const [users, setUsers] = useState([]);
    const [selectedUser, setSelectedUser] = useState(null);
    const [username, setUsername] = useState("");
    const [email, setEmail] = useState("");
    const [role, setRole] = useState("");
    const [researchArea, setResearchArea] = useState(""); // For professors
    const [password, setPassword] = useState(""); // New password field

    useEffect(() => {
        fetchUsers();
    }, []);

    // ✅ Fetch Users Function
    const fetchUsers = async () => {
        try {
            const token = localStorage.getItem("token");
            const res = await axios.get("http://localhost:8800/api/users", {
                headers: { Authorization: `Bearer ${token}` }
            });
            setUsers(res.data);
        } catch (err) {
            console.error("❌ Error fetching users:", err);
            alert("❌ Failed to fetch users.");
        }
    };

    // ✅ Handle Adding a New User
    const handleAddUser = async (e) => {
        e.preventDefault();
        try {
            const token = localStorage.getItem("token");

            // ✅ Send request to backend to create user
            await axios.post(
                "http://localhost:8800/api/register",
                {
                    username,
                    email,
                    password, // ✅ Password is now required
                    role,
                    research_area: role === "professor" ? researchArea : undefined, // ✅ Only for professors
                },
                { headers: { Authorization: `Bearer ${token}` } }
            );

            alert("✅ User added successfully!");
            fetchUsers(); // ✅ Refresh the users list
            setUsername("");
            setEmail("");
            setPassword("");
            setRole("");
            setResearchArea("");
        } catch (error) {
            console.error("❌ Error adding user:", error.response?.data || error);
            alert(error.response?.data?.message || "❌ Failed to add user.");
        }
    };

    // ✅ Handle Updating a User
    const handleUpdateUser = async () => {
        if (!selectedUser) return;
        try {
            const token = localStorage.getItem("token");
            await axios.put(
                `http://localhost:8800/api/update/${selectedUser}`,
                { username, email, role },
                { headers: { Authorization: `Bearer ${token}` } }
            );

            alert("✅ User updated successfully!");
            fetchUsers();
            setSelectedUser(null);
        } catch (err) {
            console.error("❌ Error updating user:", err);
            alert("❌ Failed to update user.");
        }
    };

    // ✅ Handle Deleting a User
    const handleDeleteUser = async (user_id) => {
        if (!window.confirm("Are you sure you want to delete this user?")) return;
        try {
            const token = localStorage.getItem("token");
            await axios.delete(`http://localhost:8800/api/delete/${user_id}`, {
                headers: { Authorization: `Bearer ${token}` }
            });

            alert("✅ User deleted successfully!");
            fetchUsers();
        } catch (err) {
            console.error("❌ Error deleting user:", err);
            alert("❌ Failed to delete user.");
        }
    };

    return (
        <div className="manage-users-container">
            <h2>Manage Users</h2>

            {/* Add User Form */}
            <div className="user-form">
                <h3>Add New User</h3>
                <form onSubmit={handleAddUser}>
                    <input type="text" placeholder="Username" value={username} onChange={(e) => setUsername(e.target.value)} required />
                    <input type="email" placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} required />
                    <input type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} required />
                    <select value={role} onChange={(e) => setRole(e.target.value)} required>
                        <option value="">Select Role</option>
                        <option value="admin">Admin</option>
                        <option value="professor">Professor</option>
                    </select>
                    {role === "professor" && (
                        <input type="text" placeholder="Research Area (Optional)" value={researchArea} onChange={(e) => setResearchArea(e.target.value)} />
                    )}
                    <button type="submit">Add User</button>
                </form>
            </div>

            {/* User List */}
            <h3>Users List</h3>
            <table className="users-table">
                <thead>
                    <tr>
                        <th>Username</th>
                        <th>Email</th>
                        <th>Role</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {users.map((user) => (
                        <tr key={user.user_id}>
                            <td>{user.user_name}</td>
                            <td>{user.user_email}</td>
                            <td>{user.role}</td>
                            <td>
                                <button onClick={() => { setSelectedUser(user.user_id); setUsername(user.user_name); setEmail(user.user_email); setRole(user.role); }}>✏️ Edit</button>
                                <button onClick={() => handleDeleteUser(user.user_id)}>🗑️ Delete</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>

            {/* Update User Section */}
            {selectedUser && (
                <div className="user-form">
                    <h3>Update User</h3>
                    <input type="text" placeholder="Username" value={username} onChange={(e) => setUsername(e.target.value)} required />
                    <input type="email" placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} required />
                    <select value={role} onChange={(e) => setRole(e.target.value)} required>
                        <option value="admin">Admin</option>
                        <option value="professor">Professor</option>
                    </select>
                    <button onClick={handleUpdateUser}>Update User</button>
                </div>
            )}
        </div>
    );
};

export default ManageUsers;
