import React, { useEffect, useState, useContext } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "./Rooms.css";
import { AuthContext } from "../auth/AuthContext";

const Rooms = () => {
    const [rooms, setRooms] = useState([]);
    const navigate = useNavigate();
    const { deptId } = useContext(AuthContext);

    useEffect(() => {
        if (!deptId) return; // ✅ Prevent unnecessary fetch calls

        const fetchRooms = async () => {
            try {
                const token = localStorage.getItem("token");
                const response = await axios.get(`http://localhost:8800/api/rooms/${deptId}`, {
                    headers: { Authorization: `Bearer ${token}` },
                });
                setRooms(response.data);
            } catch (error) {
                console.error("❌ Error fetching rooms:", error);
            }
        };

        fetchRooms();
    }, [deptId]);

    // ✅ Categorize rooms based on their type
    const classrooms = rooms.filter(room => room.room_type === "classroom");
    const labs = rooms.filter(room => room.room_type === "lab");
    const utilities = rooms.filter(room => room.room_type === "utility");

    return (
        <div className="rooms-container">
            <h2>Rooms in Your Department</h2>

            {/* ✅ Classroom Table */}
            <h3>📚 Classrooms</h3>
            {classrooms.length > 0 ? (
                <table className="rooms-table">
                    <thead>
                        <tr>
                            <th>Room No.</th>
                            <th>Room ID</th>
                            <th>Capacity</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {classrooms.map((room) => (
                            <tr key={room.room_id}>
                                <td>{room.room_num}</td>
                                <td>{room.room_id}</td>
                                <td>{room.capacity}</td>
                                <td>
                                    <button onClick={() => navigate(`/inventory/${room.room_id}`)}>
                                        View Inventory
                                    </button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            ) : (
                <p>No classrooms found.</p>
            )}

            {/* ✅ Lab Table */}
            <h3>🔬 Labs</h3>
            {labs.length > 0 ? (
                <table className="rooms-table">
                    <thead>
                        <tr>
                            <th>Room No.</th>
                            <th>Room ID</th>
                            <th>Capacity</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {labs.map((room) => (
                            <tr key={room.room_id}>
                                <td>{room.room_num}</td>
                                <td>{room.room_id}</td>
                                <td>{room.capacity}</td>
                                <td>
                                    <button onClick={() => navigate(`/inventory/${room.room_id}`)}>
                                        View Inventory
                                    </button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            ) : (
                <p>No labs found.</p>
            )}

            {/* ✅ Utility Rooms Table */}
            <h3>🛠️ Utility Rooms</h3>
            {utilities.length > 0 ? (
                <table className="rooms-table">
                    <thead>
                        <tr>
                            <th>Room No.</th>
                            <th>Room ID</th>
                            <th>Capacity</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {utilities.map((room) => (
                            <tr key={room.room_id}>
                                <td>{room.room_num}</td>
                                <td>{room.room_id}</td>
                                <td>{room.capacity}</td>
                                <td>
                                    <button onClick={() => navigate(`/inventory/${room.room_id}`)}>
                                        View Inventory
                                    </button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            ) : (
                <p>No utility rooms found.</p>
            )}
        </div>
    );
};

export default Rooms;
