import React, { useState, useEffect } from "react";
import { GoogleOAuthProvider, GoogleLogin } from "@react-oauth/google";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "./Login.css";

const Login = ({ responseGoogle }) => {
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const clientId = process.env.REACT_APP_CLIENT_ID;
    const navigate = useNavigate();

    // ‚úÖ Redirect if already logged in
    useEffect(() => {
        const storedUser = window.localStorage.getItem("user_role");

        if (storedUser) {
            if (storedUser === "super_admin") {
                navigate("/superadmin");
            } else {
                navigate("/dashboard");
            }
        }
    }, [navigate]);

    const handleSubmit = async (e) => {
        e.preventDefault();

        if (!email || !password) {
            alert("‚ùå Please fill in all fields");
            return;
        }

        try {
            const response = await axios.post("http://localhost:8800/api/login", {
                email,
                password
            });

            console.log("‚úÖ Login successful:", response.data);
            alert("üéâ Login successful!");

            const userRole = response.data.user.role; // ‚úÖ Get user role
            const userId = response.data.user.user_id;

            // ‚úÖ Store session data
            window.localStorage.setItem("user_role", userRole);
            window.localStorage.setItem("user_id", userId);

            if (userRole === "super_admin") {
                navigate("/superadmin"); // ‚úÖ Redirect Super Admins
            } else {
                navigate("/dashboard"); // ‚úÖ Redirect Others
            }

        } catch (error) {
            console.error("‚ùå Login failed:", error.response ? error.response.data : error);
            alert("‚ùå Invalid email or password.");
        }
    };

    return (
        <GoogleOAuthProvider clientId={clientId}>
            <div>
                <h2>Login</h2>
                <form onSubmit={handleSubmit} className="form-container">
                    <div>
                        <label>Email:</label>
                        <input
                            type="email"
                            value={email}
                            onChange={(e) => setEmail(e.target.value)}
                            placeholder="Enter your email"
                        />
                    </div>
                    <div>
                        <label>Password:</label>
                        <input
                            type="password"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            placeholder="Enter your password"
                        />
                    </div>
                    <button type="submit">Login</button>
                </form>

                <div className="google-login-container">
                    <GoogleLogin onSuccess={responseGoogle} onError={() => console.log("Login Failed")} />
                </div>
            </div>
        </GoogleOAuthProvider>
    );
};

export default Login;
