import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const ManageUsers = () => {
    const [users, setUsers] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        const fetchUsers = async () => {
            try {
                const res = await axios.get("http://localhost:8800/api/users");
                setUsers(res.data);
            } catch (err) {
                console.error("Error fetching users:", err);
            }
        };
        fetchUsers();
    }, []);

    const handleDelete = async (id) => {
        try {
            await axios.delete(`http://localhost:8800/api/users/${id}`);
            setUsers(users.filter(user => user.user_id !== id));
        } catch (err) {
            console.error("Error deleting user:", err);
        }
    };

    return (
        <div className="container">
            <h2>Manage Users</h2>
            <button onClick={() => navigate("/add-user")}>‚ûï Add User</button>
            <table>
                <thead>
                    <tr>
                        <th>User ID</th>
                        <th>Name</th>
                        <th>Role</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {users.map(user => (
                        <tr key={user.user_id}>
                            <td>{user.user_id}</td>
                            <td>{user.name}</td>
                            <td>{user.role}</td>
                            <td>
                                <button onClick={() => navigate(`/edit-user/${user.user_id}`)}>‚úèÔ∏è Edit</button>
                                <button onClick={() => handleDelete(user.user_id)}>üóëÔ∏è Delete</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default ManageUsers;
