import React, { useState } from "react";
import { GoogleOAuthProvider, GoogleLogin } from "@react-oauth/google";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "./Login.css";

const Login = ({ onLogin, responseGoogle }) => {
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const clientId = process.env.REACT_APP_CLIENT_ID;
    const navigate = useNavigate(); // ✅ Now `useNavigate` is used inside a Router context

    const handleSubmit = async (e) => {
        e.preventDefault();

        if (!email || !password) {
            alert("❌ Please fill in all fields");
            return;
        }

        onLogin({ email, password }, navigate); // ✅ Pass navigate to `handleLogin`
    };

    return (
        <GoogleOAuthProvider clientId={clientId}>
            <div>
                <h2>Login</h2>
                <form onSubmit={handleSubmit} className="form-container">
                    <div>
                        <label>Email:</label>
                        <input
                            type="email"
                            value={email}
                            onChange={(e) => setEmail(e.target.value)}
                            placeholder="Enter your email"
                        />
                    </div>
                    <div>
                        <label>Password:</label>
                        <input
                            type="password"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            placeholder="Enter your password"
                        />
                    </div>
                    <button type="submit">Login</button>
                </form>

                <div className="google-login-container">
                    <GoogleLogin onSuccess={responseGoogle} onError={() => console.log("Login Failed")} />
                </div>
            </div>
        </GoogleOAuthProvider>
    );
};

export default Login;
